import React from "react";

function ArtistDashboard() {
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Welcome to ArtFusion</h1>
      <p style={styles.text}>We are glad to have you here!</p>
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: "#007bff", // âœ… Blue background
    color: "white",
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
  },
  title: {
    fontSize: "3rem",
    fontWeight: "bold",
  },
  text: {
    fontSize: "1.5rem",
  },
};

export default ArtistDashboard;
