import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const OrderDetails = () => {
  const { orderId } = useParams();
  const [payment, setPayment] = useState(null);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/payments/${orderId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        console.log("✅ Payment Response:", response.data);
        setPayment(response.data);
      } catch (error) {
        console.error("❌ Failed to fetch order:", error);
      }
    };

    if (orderId) {
      fetchOrder();
    }
  }, [orderId]);

  if (!payment) {
    return <h2 style={{ textAlign: "center", marginTop: "50px" }}>Loading order details...</h2>;
  }

  const artwork = payment.order?.artwork || {};
  const order = payment.order || {};

  return (
    <div style={{ padding: "20px" }}>
      <h2>Order Details</h2>
      <div
        style={{
          width: "300px",
          padding: "15px",
          borderRadius: "10px",
          backgroundColor: "#f0f0f0",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        <img
          src={artwork.imageUrl}
          alt={artwork.title}
          style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "8px" }}
        />
        <h3>{artwork.title}</h3>
        <p><strong>Artist:</strong> {artwork.artist?.username}</p>
        <p><strong>Price:</strong> ₹{artwork.price}</p>
        <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>
        <p><strong>Transaction ID:</strong> {payment.transactionId}</p>
        <p><strong>Status:</strong> {payment.status}</p>
      </div>
    </div>
  );
};

export default OrderDetails;
