    <div
  style={{
    display: "flex",
    flexDirection: "column",
    alignItems: "flex-start", // Align everything to the left
    gap: "20px",
    marginTop: "20px",
    paddingLeft: "50px", // Move everything a bit to the left
  }}
>
          {artworks.map((art) => (
      <div
      key={art.id}
      style={{
        display: "flex",
        flexDirection: "column", // Stack image and text vertically
        alignItems: "flex-start",
        backgroundColor: "rgba(255, 255, 255, 0.9)",
        borderRadius: "10px",
        overflow: "hidden",
        boxShadow: "0px 4px 12px rgba(0,0,0,0.2)",
        width: "300px", // Adjust width for better vertical alignment
        padding: "15px",
        transition: "transform 0.3s ease-in-out",
      }}
    >
      {/* Spotlight Gradient Effect */}
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          width: "140%",
          height: "140%",
          background: "radial-gradient(circle, rgba(255,255,255,0.3) 20%, rgba(0,0,0,0.8) 100%)",
          transform: "translate(-50%, -50%)",
          zIndex: "-1",
        }}
      ></div>
         {/* Image (Top)
      <img
        src={art.imageUrl}
        alt="Art"
