              type="text"
              name="title"
              placeholder="Title"
              value={formData.title}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            />
            <input
              type="text"
              name="category"
              placeholder="Category"
              value={formData.category}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={formData.description}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            />
             <input
              type="text"
              name="imageUrl"
              placeholder="Image URL"
              value={formData.imageUrl}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            /> 
            

            <input
              type="number"
              name="price"
              placeholder="Price"
              value={formData.price}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            />
  
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <button type="submit" style={{ background: "#28a745", color: "white", padding: "8px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
                Upload
              </button>
              <button type="button" onClick={() => setIsDialogOpen(false)} style={{ background: "#dc3545", color: "white", padding: "8px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};
