import React from "react";
import { useLocation } from "react-router-dom";
import "./ArtistDashboard.css"; // Ensure styles are managed correctly

const ArtistDashboard = () => {
  const location = useLocation(); // Get current route

  return (
    <div className="artist-dashboard">
      {/* ‚úÖ Show background video & banner ONLY on homepage */}
      {location.pathname === "/" && (
        <div className="banner">
          <video autoPlay loop muted className="background-video">
            <source src="/assets/video.mp4" type="video/mp4" />
          </video>
          <div className="overlay-text">
            <h1>Welcome To ArtFusion</h1>
            <p>Art is not what you see, but what makes others see</p>
            <p>Explore amazing content!</p>
          </div>
        </div>
      )}

      {/* ‚úÖ Art Gallery Section - Always Visible */}
      <div className="art-gallery">
        <h2> Art Gallery</h2>
        {/* Your Art Gallery content here */}
      </div>
    </div>
  );
};

export default ArtistDashboard;
