import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom"; // ✅ React Router v5
import { FaUserCircle } from "react-icons/fa"; // ✅ Profile icon

const UserDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const history = useHistory();

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const response = await axios.get("/api/artwork");
        setArtworks(response.data);
      } catch (error) {
        console.error("Error fetching artworks:", error);
      }
    };

    fetchArtworks();
  }, []);

  const handleAddToCart = (artwork) => {
    console.log("Adding to cart:", artwork);
    history.push("/cart");
  };

  const filteredArtworks = artworks.filter(
    (art) =>
      art.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      art.artist?.username?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-b from-sky-950 to-black text-white px-6 py-10">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Explore Artworks</h1>
          <div className="flex items-center gap-4">
            <input
              type="text"
              placeholder="Search by title or artist"
              className="p-2 rounded text-black w-64"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <FaUserCircle
              size={30}
              className="cursor-pointer hover:text-gray-300"
              onClick={() => history.push("/profile")}
              title="Go to Profile"
            />
          </div>
        </div>

        {/* Artworks Grid */}
        <div className="grid gap-8 grid-cols-1 sm:grid-cols-2 md:grid-cols-3">
          {filteredArtworks.map((art) => (
            <div
              key={art.id}
              className="bg-white text-black rounded-xl overflow-hidden shadow-lg"
            >
              <img
                src={art.imageUrl}
                alt={art.title}
                className="w-full h-52 object-cover"
              />
              <div className="p-4">
                <h2 className="text-xl font-bold">{art.title}</h2>
                <p className="text-sm text-gray-600">{art.description}</p>
                <p className="mt-2 font-medium text-blue-700">
                  ₹{art.price?.toFixed(2)}
                </p>
                <p className="text-sm text-gray-700">
                  Artist: {art.artist?.username}
                </p>
                <button
                  className="mt-3 bg-blue-600 hover:bg-blue-700 text-white py-1 px-3 rounded"
                  onClick={() => handleAddToCart(art)}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))}

          {filteredArtworks.length === 0 && (
            <p className="col-span-full text-center text-lg text-red-400">
              No artworks found.
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
