import React from "react";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Header from "./components/Header";
import BackgroundVideo from "./components/BackgroundVideo";
import Dashboard from "./components/board/Dashboard";
import Profile from "./components/Profile";
import ArtistDashboard from "./components/board/ArtistDashboard";
import Login from "./components/auth/Login"; // ✅ Ensure Login component is imported

function App() {
  const userRole = (localStorage.getItem("role") || "").toLowerCase(); // ✅ Convert to lowercase

  return (
    <Router>
      <div className="app-container">
        <BackgroundVideo />
        <Header />

        <Switch>
          <Route exact path="/">
            <Redirect to="/dashboard" />
          </Route>
          <Route path="/dashboard" component={Dashboard} />
          <Route path="/profile" component={Profile} />
          <Route path="/login" component={Login} /> {/* ✅ Added Login route */}

          {/* ✅ Fix: Match artist route correctly */}
          <Route
            path="/artist-dashboard"
            render={(props) =>
              userRole === "artist" ? <ArtistDashboard {...props} /> : <Redirect to="/login" />
            }
          />

        </Switch>
      </div>
    </Router>
  );
}

export default App;
