import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  BsCart3, BsGrid1X2Fill, BsFillArchiveFill, BsFillGrid3X3GapFill,
  BsPeopleFill, BsListCheck, BsMenuButtonWideFill, BsFillGearFill
} from 'react-icons/bs';

function Sidebar({ openSidebarToggle, OpenSidebar }) {
  const [showOrders, setShowOrders] = useState(false);
  const [orders, setOrders] = useState([]);
  const [statusUpdate, setStatusUpdate] = useState({}); // Track status input for each order

  const token = localStorage.getItem('token');

  useEffect(() => {
    if (showOrders) {
      axios.get('http://localhost:8080/api/admin/dashboard/orders', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
      .then(response => setOrders(response.data))
      .catch(err => console.error('Error fetching orders:', err));
    }
  }, [showOrders]);

  const handleStatusChange = (orderId, newStatus) => {
    axios.put(`http://localhost:8080/api/admin/dashboard/orders/${orderId}/status/${newStatus}`, {}, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(() => {
      alert(`Order ${orderId} status updated to ${newStatus}`);
      setOrders(prev =>
        prev.map(order => order.id === orderId ? { ...order, orderStatus: newStatus } : order)
      );
    })
    .catch(err => console.error('Failed to update order status:', err));
  };

  return (
    <aside id="sidebar" className={openSidebarToggle ? "sidebar-responsive" : ""}>
      <div className='sidebar-title'>
        <div className='sidebar-brand'>
          <BsCart3 className='icon_header' /> art fusion 
        </div>
        <span className='icon close_icon' onClick={OpenSidebar}>X</span>
      </div>

      <ul className='sidebar-list'>
        <li className='sidebar-list-item'>
          <a onClick={() => window.location.href = "/admin-dashboard"}>
            <BsGrid1X2Fill className='icon' /> Dashboard
          </a>
        </li>
        <li className='sidebar-list-item'>
          <a onClick={() => window.location.href = "/admin-dashboard/artworks"}>
            <BsFillArchiveFill className='icon' /> Artwork
          </a>
        </li>
        <li className='sidebar-list-item'>
          <a onClick={() => window.location.href = "/admin-dashboard/themes"}>
            <BsFillGrid3X3GapFill className='icon' /> Themes
          </a>
        </li>
        <li className='sidebar-list-item'>
          <a onClick={() => window.location.href = "/admin-dashboard/customer"}>
            <BsPeopleFill className='icon' /> Customers
          </a>
        </li>
        <li className='sidebar-list-item'>
          <a onClick={() => setShowOrders(!showOrders)}>
            <BsListCheck className='icon' /> Order Status
          </a>
        </li>
        <li className='sidebar-list-item'>
          <a onClick={() => window.location.href = "/admin/reports"}>
            <BsMenuButtonWideFill className='icon' /> Reports
          </a>
        </li>
        <li className='sidebar-list-item'>
          <a onClick={() => window.location.href = "/admin/settings"}>
            <BsFillGearFill className='icon' /> Settings
          </a>
        </li>
      </ul>

      {showOrders && (
        <div className="order-table" style={{ padding: '1rem', overflowX: 'auto' }}>
          <h3>All Orders</h3>
          <table border="1" cellPadding="10">
            <thead>
              <tr>
                <th>ID</th>
                <th>Status</th>
                <th>Order Date</th>
                <th>Total Price</th>
                <th>Update Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.orderStatus}</td>
                  <td>{order.orderDate}</td>
                  <td>{order.totalPrice}</td>
                  <td>
                    <select
                      value={statusUpdate[order.id] || order.orderStatus}
                      onChange={(e) => {
                        const newStatus = e.target.value;
                        setStatusUpdate({ ...statusUpdate, [order.id]: newStatus });
                        handleStatusChange(order.id, newStatus);
                      }}
                    >
                      <option value="PENDING">PENDING</option>
                      <option value="COMPLETED">COMPLETED</option>
                      <option value="FAILED">FAILED</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </aside>
  );
}

export default Sidebar;
