import React, { useState } from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "axios";

const CheckoutForm = ({ artworkId }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [downloadUrl, setDownloadUrl] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) return;

    // Step 1: Confirm the payment using Stripe's confirmCardPayment method
    const result = await stripe.confirmCardPayment(elements._clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (result.error) {
      console.error("❌ Payment failed:", result.error.message);
      alert("Payment failed!");
      setErrorMessage(result.error.message);
    } else {
      if (result.paymentIntent.status === "succeeded") {
        alert("✅ Payment successful!");

        // Step 2: If payment is successful, send a request to the backend for download link
        try {
          // Assuming `artworkId` is passed as a prop to CheckoutForm
          const response = await axios.get(`http://localhost:8080/api/artworks/download/${artworkId}`);

          // Check if response contains artworkUrl
          if (response.status === 200 && response.data.artworkUrl) {
            setDownloadUrl(response.data.artworkUrl);  // Store the download URL
          } else {
            setErrorMessage("You are not eligible to download this artwork.");
          }
        } catch (error) {
          console.error("❌ Error fetching download link:", error);
          setErrorMessage("An error occurred while checking download eligibility.");
        }
      }
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} style={{ marginTop: "30px" }}>
        <CardElement />
        <button type="submit" disabled={!stripe} style={{ marginTop: "20px" }}>
          Pay Now
        </button>
      </form>

      {/* Step 3: Show download button if payment was successful and URL is available */}
      {downloadUrl && (
        <div>
          <h3>Your payment was successful!</h3>
          <button
            onClick={() => {
              window.location.href = downloadUrl; // Trigger file download
            }}
            style={{ marginTop: "20px" }}
          >
            Download Artwork
          </button>
        </div>
      )}

      {/* Display error message if any */}
      {errorMessage && (
        <div style={{ color: "red", marginTop: "20px" }}>
          <p>{errorMessage}</p>
        </div>
      )}
    </div>
  );
};

export default CheckoutForm;
