import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import axios from 'axios';

const OrderDetails = () => {
  const { orderId } = useParams();
  const stripe = useStripe();
  const elements = useElements();

  const [paymentData, setPaymentData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPaymentData = async () => {
      try {
        const response = await axios.get(`/api/payment/${orderId}`);
        setPaymentData(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching payment details:', error);
        setError('Failed to fetch payment details');
        setLoading(false);
      }
    };

    fetchPaymentData();
  }, [orderId]);

  const handlePayment = async () => {
    if (!stripe || !elements) {
      return;
    }

    try {
      const { data: clientSecretResponse } = await axios.post('/api/payment/confirm', {
        transactionId: paymentData.transactionId,
      });

      const clientSecret = clientSecretResponse.clientSecret;

      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
        },
      });

      if (result.error) {
        console.error(result.error.message);
        setError(result.error.message);
      } else {
        if (result.paymentIntent.status === 'succeeded') {
          setPaymentSuccess(true);
          setError('');
        }
      }
    } catch (error) {
      console.error('Payment error:', error);
      setError('Payment failed');
    }
  };

  if (loading) {
    return <div className="text-center text-xl font-semibold">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-red-500 text-xl">{error}</div>;
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4 bg-gray-50">
      <div className="bg-white p-8 rounded-2xl shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-center">Order Details</h2>

        <div className="space-y-4 mb-6">
          <div><strong>Transaction ID:</strong> {paymentData.transactionId}</div>
          <div><strong>Amount:</strong> ${paymentData.amount / 100}</div>
          <div><strong>Artwork ID:</strong> {paymentData.artworkId ? paymentData.artworkId : 'N/A'}</div>
        </div>

        {!paymentSuccess ? (
          <>
            <div className="mb-6">
              <CardElement className="p-2 border rounded-md" />
            </div>
            <button
              onClick={handlePayment}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              disabled={!stripe || !elements}
            >
              Pay Now
            </button>
          </>
        ) : (
          <div className="text-center">
            <p className="text-green-600 text-xl mb-4">Payment Successful!</p>
            <a
              href={`/download/${paymentData.artworkId}`}
              className="inline-block bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded"
            >
              Download Artwork
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderDetails;
