const Login = ({ open = false, onClose = () => {}, onLogin = () => {} }) => { 
  const history = useHistory();  
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [message, setMessage] = useState({ text: "", type: "error" });
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("üîµ Sending Data:", formData);

    try {
      const response = await axios.post("http://localhost:8080/api/users/login", formData, {
        headers: { "Content-Type": "application/json" },
        withCredentials: true,
      });

      console.log("üü¢ Full Response:", response);
      console.log("üü¢ Response Data:", JSON.stringify(response.data, null, 2));

      const { token, role } = response.data;

      if (token) {
        localStorage.setItem("token", token);
        localStorage.setItem("username", formData.username);
        if (role) {
          localStorage.setItem("role", role);
        }

        console.log("üîÄ Redirecting based on role:", role);
        onLogin(token);
        setMessage({ text: "‚úÖ Login successful! Redirecting...", type: "success" });
        setSnackbarOpen(true);

        setTimeout(() => {
          onClose();  // ‚úÖ Close the login dialog
          if (role && role.toLowerCase() === "artist") {
            console.log("üé® Redirecting to /artist-dashboard");
            history.push("/artist-dashboard");
          } else {
            console.log("üè† Redirecting to /dashboard");
            history.push("/dashboard");
          }
        }, 1500);
      } else {
        console.error("‚ùå API did not return token. Response Data:", response.data);
        setMessage({ text: "‚ùå Login failed. No token received.", type: "error" });
        setSnackbarOpen(true);
      }
    } catch (error) {
      console.error("üî¥ Full API Error:", error);

      if (error.response) {
        console.log("üî¥ Error Response Data:", error.response.data);
        setMessage({ text: error.response.data?.message || "‚ùå Invalid username or password", type: "error" });
      } else if (error.request) {
        setMessage({ text: "‚ö†Ô∏è Server not responding. Try again later.", type: "error" });
      } else {
        setMessage({ text: "‚ùå Request failed. Check network.", type: "error" });
      }
      setSnackbarOpen(true);
    }
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>
        Login
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{ position: "absolute", right: 8, top: 8 }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <TextField 
          label="Username" 
          name="username" 
          type="text" 
          fullWidth 
          margin="dense" 
          value={formData.username} 
          onChange={handleChange} 
          required
        />
        <TextField 
          label="Password" 
          name="password" 
          type="password" 
          fullWidth 
          margin="dense" 
          value={formData.password} 
          onChange={handleChange} 
          required
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary" variant="contained">Login</Button>
      </DialogActions>

      {/* Snackbar for Feedback */}
      <Snackbar open={snackbarOpen} autoHideDuration={3000} onClose={() => setSnackbarOpen(false)}>
        <Alert onClose={() => setSnackbarOpen(false)} severity={message.type} sx={{ width: '100%' }}>
          {message.text}
        </Alert>
      </Snackbar>
    </Dialog>
  );
};

export default Login;
