import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import axios from 'axios';

const CARD_ELEMENT_OPTIONS = {
  style: {
    base: {
      fontSize: '16px',
      color: '#32325d',
      '::placeholder': {
        color: '#a0aec0',
      },
    },
    invalid: {
      color: '#e53e3e',
    },
  },
};

const OrderDetails = () => {
  const { orderId } = useParams();
  const numericOrderId = Number(orderId);
  const stripe = useStripe();
  const elements = useElements();

  const [payment, setPayment] = useState(null);
  const [clientSecret, setClientSecret] = useState('');
  const [loading, setLoading] = useState(true);
  const [paymentStatus, setPaymentStatus] = useState('');
  const [error, setError] = useState('');
  const [downloadUrl, setDownloadUrl] = useState('');
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchPaymentDetails = async () => {
      try {
        const intentRes = await axios.post(
          'http://localhost:8080/api/payments/create-payment-intent',
          { orderId: numericOrderId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setClientSecret(intentRes.data.clientSecret);

        const paymentRes = await axios.get(
          `http://localhost:8080/api/payments/${orderId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        const paymentData = paymentRes.data;
        setPayment(paymentData);

        // Prepare artwork download URL if available
        if (paymentData.artworkId) {
          setDownloadUrl(`http://localhost:8080/api/artworks/download/${paymentData.artworkId}`);
        }

        setLoading(false);
      } catch (err) {
        console.error('Error fetching payment details:', err);
        setError(
          err.response?.status === 403
            ? 'You are not authorized to view this order.'
            : 'Something went wrong. Please try again.'
        );
        setLoading(false);
      }
    };

    fetchPaymentDetails();
  }, [orderId, token, numericOrderId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (result.error) {
      setError(result.error.message);
    } else {
      const status = result.paymentIntent.status;
      const mappedStatus =
        status === 'succeeded'
          ? 'COMPLETED'
          : status === 'processing'
          ? 'PENDING'
          : 'FAILED';

      try {
        const confirmRes = await axios.post(
          'http://localhost:8080/api/payments/confirm',
          {
            transactionId: result.paymentIntent.id,
            status: mappedStatus,
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setPaymentStatus(
          mappedStatus === 'COMPLETED'
            ? 'Payment successful!'
            : 'Payment submitted but not completed.'
        );

        // Ensure artworkId is present to form download URL
        if (mappedStatus === 'COMPLETED' && payment?.artworkId) {
          setDownloadUrl(`http://localhost:8080/api/artworks/download/${payment.artworkId}`);
        }

      } catch (err) {
        console.error('Error confirming payment:', err);
        setError('Failed to confirm payment on the server.');
      }
    }
  };

  if (loading) return <p style={{ textAlign: 'center', marginTop: '20px', color: 'white' }}>Loading...</p>;
  if (error) return <p style={{ textAlign: 'center', marginTop: '20px', color: 'red' }}>{error}</p>;

  return (
    <div
      style={{
        padding: "20px",
        backgroundImage: `url("/artist.png")`,
        backgroundSize: "100% 100%",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        backgroundAttachment: "fixed",
        minHeight: "100vh",
        width: "100vw",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div style={{
        width: '100%',
        maxWidth: '500px',
        background: 'rgba(0, 0, 0, 0.75)',
        padding: '30px',
        borderRadius: '12px',
        color: 'white',
      }}>
        <h2 style={{ marginBottom: '20px' }}>Order Payment</h2>

        <div style={{ marginBottom: '20px' }}>
          <p><strong>Transaction ID:</strong> {payment?.transactionId}</p>
          <p><strong>Amount:</strong> ₹{payment?.amount}</p>
        </div>

        <form onSubmit={handleSubmit}>
          <div style={{ padding: '12px', border: '1px solid #ccc', borderRadius: '8px', marginBottom: '15px', background: 'white' }}>
            <CardElement options={CARD_ELEMENT_OPTIONS} />
          </div>

          <button
            type="submit"
            disabled={!stripe || !elements}
            style={{
              width: '100%',
              backgroundColor: '#007bff',
              color: 'white',
              padding: '10px 16px',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontWeight: 'bold',
              fontSize: '16px',
            }}
          >
            Pay Now
          </button>
        </form>

        {paymentStatus && (
          <p style={{ marginTop: '20px', textAlign: 'center', color: 'lightgreen', fontWeight: 'bold' }}>{paymentStatus}</p>
        )}

        {paymentStatus === 'Payment successful!' && downloadUrl && (
          <div style={{ marginTop: '20px', textAlign: 'center' }}>
            <a
              href={downloadUrl}
              download
              style={{
                display: 'inline-block',
                backgroundColor: '#28a745',
                color: 'white',
                padding: '10px 16px',
                borderRadius: '6px',
                textDecoration: 'none',
                fontWeight: 'bold',
              }}
            >
              Download Artwork
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderDetails;
