// OrderDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import CheckoutForm from './CheckoutForm';

const OrderDetails = () => {
  const { orderId } = useParams();
  const token = localStorage.getItem('token');

  const [payment, setPayment] = useState(null);
  const [clientSecret, setClientSecret] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const intentRes = await axios.post(
          'http://localhost:8080/api/payments/create-payment-intent',
          { orderId: Number(orderId) },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setClientSecret(intentRes.data.clientSecret);

        const paymentRes = await axios.get(
          `http://localhost:8080/api/payments/${orderId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setPayment(paymentRes.data);
      } catch (err) {
        console.error(err);
        setError('Something went wrong while fetching payment details.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [orderId, token]);

  if (loading) return <p className="text-center text-white mt-10">Loading...</p>;
  if (error) return <p className="text-center text-red-500 mt-10">{error}</p>;

  return (
    <div
      className="flex justify-center items-center min-h-screen px-4"
      style={{
        padding: "20px",
        backgroundImage: `url("/artist.png")`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="w-full max-w-md bg-black bg-opacity-80 rounded-2xl shadow-xl p-8">
        <h2 className="text-white text-2xl mb-4 text-center">Order Payment</h2>

        <div className="text-white mb-4 space-y-2">
          <p><strong>Transaction ID:</strong> {payment.transactionId}</p>
          <p><strong>Amount:</strong> ₹{payment.amount}</p>
        </div>

        <CheckoutForm
          clientSecret={clientSecret}
          orderId={orderId}
          artworkId={payment.artworkId}
        />
      </div>
    </div>
  );
};

export default OrderDetails;
