import React, { useState } from "react";
import { 
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, IconButton, Snackbar, Alert 
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import axios from "axios";
import { useHistory } from "react-router-dom"; // ✅ Use useHistory for React Router v5

const Login = ({ open, onClose, onLogin }) => {
  const history = useHistory(); // ✅ Initialize history for navigation
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [message, setMessage] = useState({ text: "", type: "error" });
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.username || !formData.password) {
      setMessage({ text: "❌ Username and Password are required!", type: "error" });
      setSnackbarOpen(true);
      return;
    }

    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_BASE_URL}api/users/login`, 
        formData
      );

      // ✅ Store JWT token
      localStorage.setItem("token", response.data.token);

      // ✅ Update authentication state
      onLogin(response.data.token);

      setMessage({ text: "✅ Login successful! Redirecting...", type: "success" });
      setSnackbarOpen(true);

      // ✅ Redirect to dashboard (React Router v5 syntax)
      setTimeout(() => {
        history.push("/dashboard"); // ✅ Use history.push() instead of navigate()
      }, 1500);
    } catch (error) {
      setMessage({ 
        text: error.response?.data?.message || "❌ Invalid username or password", 
        type: "error" 
      });
      setSnackbarOpen(true);
    }
  };

  // Handle dialog close
  const handleClose = () => {
    setFormData({ username: "", password: "" });
    setMessage({ text: "", type: "error" });
    onClose();
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>
        Login
        <IconButton
          aria-label="close"
          onClick={handleClose}
          sx={{ position: "absolute", right: 8, top: 8 }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <TextField 
          label="Username" 
          name="username" 
          type="text" 
          fullWidth 
          margin="dense" 
          value={formData.username} 
          onChange={handleChange} 
          required
        />
        <TextField 
          label="Password" 
          name="password" 
          type="password" 
          fullWidth 
          margin="dense" 
          value={formData.password} 
          onChange={handleChange} 
          required
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="secondary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary" variant="contained">Login</Button>
      </DialogActions>

      {/* Snackbar for Feedback */}
      <Snackbar open={snackbarOpen} autoHideDuration={3000} onClose={() => setSnackbarOpen(false)}>
        <Alert onClose={() => setSnackbarOpen(false)} severity={message.type} sx={{ width: '100%' }}>
          {message.text}
        </Alert>
      </Snackbar>
    </Dialog>
  );
};

export default Login;
