// export default Header;
import React from 'react';
import { BsJustify, BsSearch, BsPersonCircle } from 'react-icons/bs';

function Header() {
  return (
    <header
      style={{
        height: '60px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0 30px',
        backgroundColor: '#1e1e2f',
        color: 'white',
        position: 'fixed',
        top: 0,
        left: '250px', // adjust if sidebar width changes
        right: 0,
        zIndex: 1000,
        boxShadow: '0 6px 7px -3px rgba(0, 0, 0, 0.35)',
      }}
    >
      {/* Left - Menu Icon */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        <BsJustify style={{ fontSize: '1.5rem', cursor: 'pointer' }} />
      </div>

      {/* Right - Search and Profile Icons */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        <BsSearch style={{ fontSize: '1.5rem', cursor: 'pointer' }} />
        <BsPersonCircle style={{ fontSize: '1.5rem', cursor: 'pointer' }} />
      </div>
    </header>
  );
}

export default Header;
