import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [isUpdateMode, setIsUpdateMode] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    category: "",
    description: "",
    imageUrl: "",
    price: "",
  });
  const [earnings, setEarnings] = useState(null);
  const [isEarningsDialogOpen, setIsEarningsDialogOpen] = useState(false);

  const history = useHistory();

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;
      const response = await axios.get("http://localhost:8080/api/artworks/my-artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data);
    } catch (error) {
      console.error("ðŸ”´ Error fetching artworks:", error);
    }
  };
  const fetchEarnings = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;
      const response = await axios.get("http://localhost:8080/api/orders/earnings", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEarnings(response.data); // Set the earnings value
      setIsEarningsDialogOpen(true); // Open the earnings dialog
    } catch (error) {
      console.error("ðŸ”´ Error fetching earnings:", error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      if (isUpdateMode && selectedArtwork) {
        // Update existing artwork
        await axios.put(`http://localhost:8080/api/artworks/${selectedArtwork.id}`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });
        alert("âœ… Artwork updated successfully!");
      } else {
        // Upload new artwork
        await axios.post("http://localhost:8080/api/artworks/upload", formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });
        alert("âœ… Artwork uploaded successfully!");
      }

      fetchArtworks();
      setFormData({ title: "", category: "", description: "", imageUrl: "", price: "" });
      setIsDialogOpen(false);
      setIsUpdateMode(false);
      setSelectedArtwork(null);
    } catch (error) {
      console.error("ðŸ”´ Error submitting artwork:", error);
    }
  };

  const handleDeleteArtwork = async () => {
    if (!selectedArtwork) return;
    const confirmDelete = window.confirm("Are you sure you want to delete this artwork?");
    if (!confirmDelete) return;

    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:8080/api/artworks/${selectedArtwork.id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("âœ… Artwork deleted successfully!");
      setArtworks((prev) => prev.filter((art) => art.id !== selectedArtwork.id));
      setSelectedArtwork(null);
    } catch (error) {
      console.error("ðŸ”´ Error deleting artwork:", error);
    }
  };

  const handleEditArtwork = () => {
    if (selectedArtwork) {
      setFormData({
        title: selectedArtwork.title,
        category: selectedArtwork.category,
        description: selectedArtwork.description,
        imageUrl: selectedArtwork.imageUrl,
        price: selectedArtwork.price,
      });
      setIsUpdateMode(true);
      setIsDialogOpen(true);
    }
  };

  const handleMyEarnings = () => {
    // Placeholder for earnings functionality
    alert("Viewing your earnings is under development!");
  };

  return (
    <div
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/midnight_blue_wall_bg.png)`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        padding: "20px",
        opacity: 0.9,
      }}
    >
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h1 style={{ color: "white", fontSize: "28px", fontWeight: "bold" }}>
          Welcome to Art Gallery!
        </h1>
        <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <button
            onClick={() => {
              setIsDialogOpen(true);
              setIsUpdateMode(false);
              setFormData({ title: "", category: "", description: "", imageUrl: "", price: "" });
            }}
            style={{
              backgroundColor: "#007bff",
              color: "white",
              padding: "10px 15px",
              borderRadius: "5px",
              border: "none",
              cursor: "pointer",
            }}
          >
            Sell Your Art
          </button>

          <button
            onClick={fetchEarnings} // Trigger earnings fetch on button click
            style={{
              backgroundColor: "#28a745",
              color: "white",
              padding: "10px 15px",
              borderRadius: "5px",
              border: "none",
              cursor: "pointer",
            }}
          >
            My Earnings
          </button>

          <div style={{ position: "relative" }}>
            <button
              style={{ backgroundColor: "transparent", border: "none", cursor: "pointer", fontSize: "20px" }}
              onClick={() => setShowProfileDropdown(!showProfileDropdown)}
            >
              ðŸ§‘
            </button>
            {showProfileDropdown && (
              <div
                style={{
                  position: "absolute",
                  top: "40px",
                  right: "0",
                  background: "white",
                  boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                  borderRadius: "5px",
                  padding: "10px",
                  width: "120px",
                  textAlign: "center",
                }}
              >
                <p style={{ margin: "5px 0", cursor: "pointer",color:"black" }} onClick={() => history.push("/profile")}>
                  Profile
                </p>
                <p style={{ margin: "5px 0", cursor: "pointer",color:"black" }} onClick={()=>history.push("/login")}>Logout</p>
              </div>
            )}
          </div>
        </div>
      </div>
      {isEarningsDialogOpen && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "400px",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
            zIndex: 1000,
            color:"black"
          }}
        >
          <h2 >Your Total Earnings</h2>
          <p style={{ fontSize: "24px", fontWeight: "bold",color:"black" }}>â‚¹{earnings}</p>
          <button
            onClick={() => setIsEarningsDialogOpen(false)}
            style={{
              background: "#dc3545",
              color: "white",
              padding: "8px",
              borderRadius: "5px",
              border: "none",
              width: "100%",
            }}
          >
            Close
          </button>
        </div>
      )}

      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          gap: "20px",
          marginTop: "20px",
        }}
      >
        {artworks.map((art) => (
          <div
            key={art.id}
            style={{
              position: "relative",
              width: "280px",
              borderRadius: "10px",
              overflow: "hidden",
              cursor: "pointer",
            }}
            onClick={() => setSelectedArtwork(art)}
          >
            <img
              src={art.imageUrl}
              alt={art.title}
              style={{
                width: "90%",
                height: "200px",
                objectFit: "cover",
                borderRadius: "10px",
                boxShadow: "0px 4px 15px rgba(255, 215, 0, 0.8)",
                border: "2px solid gold",
              }}
            />
          </div>
        ))}
      </div>

      {isDialogOpen && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "400px",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
            zIndex: 1000,
            color:"black"
          }}
        >
          <h2>{isUpdateMode ? "Update Artwork" : "Sell Your Art"}</h2>
          <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            <input type="text" name="title" placeholder="Title" value={formData.title} onChange={handleInputChange} required />
            <input type="text" name="category" placeholder="Category" value={formData.category} onChange={handleInputChange} required />
            <textarea name="description" placeholder="Description" value={formData.status} onChange={handleInputChange} required />
            <input type="text" name="imageUrl" placeholder="Image URL" value={formData.imageUrl} onChange={handleInputChange} required />
            <input type="number" name="price" placeholder="Price" value={formData.price} onChange={handleInputChange} required />
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <button type="submit" style={{ background: "#28a745", color: "white", padding: "8px", borderRadius: "5px", border: "none" }}>
                {isUpdateMode ? "Update" : "Upload"}
              </button>
              <button
                type="button"
                onClick={() => {
                  setIsDialogOpen(false);
                  setIsUpdateMode(false);
                  setSelectedArtwork(null);
                }}
                style={{ background: "#dc3545", color: "white", padding: "8px", borderRadius: "5px", border: "none" }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {selectedArtwork && !isDialogOpen && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "400px",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
            zIndex: 1000,
            color: "black",
          }}
        >
          <h3>{selectedArtwork.title}</h3>
          <p><strong>Category:</strong> {selectedArtwork.category}</p>
          <p><strong>Description:</strong> {selectedArtwork.description}</p>
          <p style={{ fontWeight: "bold", fontSize: "18px" }}>Price:â‚¹{selectedArtwork.price}</p>
          <div style={{ display: "flex", justifyContent: "space-between", marginTop: "10px" }}>
            <button onClick={() => setSelectedArtwork(null)} style={{ background: "#dc3545", color: "white", padding: "8px", borderRadius: "5px" }}>
              Close
            </button>
            <button onClick={handleEditArtwork} style={{ background: "#ffc107", color: "black", padding: "8px", borderRadius: "5px" }}>
              Update
            </button>
            <button onClick={handleDeleteArtwork} style={{ background: "red", color: "white", padding: "8px", borderRadius: "5px" }}>
              Delete
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
