import React, { useState } from "react";
import {
  Dialog, DialogTitle, DialogContent, DialogActions,
  Button, TextField, IconButton, Snackbar, Alert
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import axios from "axios";

const Login = ({ open = false, onClose = () => {}, onLogin = () => {} }) => {
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [message, setMessage] = useState({ text: "", type: "error" });
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.username || !formData.password) {
      setMessage({ text: "‚ùå Please enter both username and password", type: "error" });
      setSnackbarOpen(true);
      return;
    }

    try {
      const response = await axios.post("http://localhost:8080/api/users/login", formData, {
        headers: {
          "Content-Type": "application/json",
        },
      });

      const { token, userId, role } = response.data;

      if (token && userId && role) {
        // Save to local storage
        localStorage.setItem("token", token);
        localStorage.setItem("userId", userId);
        localStorage.setItem("role", role);

        setMessage({ text: "‚úÖ Login successful!", type: "success" });
        setSnackbarOpen(true);

        // Pass data to Header
        onLogin(token, userId, role);

        // Optional: close the login dialog
        onClose();
      } else {
        setMessage({ text: "‚ùå Login failed: Incomplete response from server", type: "error" });
        setSnackbarOpen(true);
      }
    } catch (error) {
      console.error("üî¥ Login API error:", error);
      const errorMsg = error?.response?.data?.message || "‚ùå Invalid credentials or server error.";
      setMessage({ text: errorMsg, type: "error" });
      setSnackbarOpen(true);
    }
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>
        Login
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{ position: "absolute", right: 8, top: 8 }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <TextField
          label="Username"
          name="username"
          type="text"
          fullWidth
          margin="dense"
          value={formData.username}
          onChange={handleChange}
          required
        />
        <TextField
          label="Password"
          name="password"
          type="password"
          fullWidth
          margin="dense"
          value={formData.password}
          onChange={handleChange}
          required
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary" variant="contained">Login</Button>
      </DialogActions>

      <Snackbar open={snackbarOpen} autoHideDuration={3000} onClose={() => setSnackbarOpen(false)}>
        <Alert onClose={() => setSnackbarOpen(false)} severity={message.type} sx={{ width: '100%' }}>
          {message.text}
        </Alert>
      </Snackbar>
    </Dialog>
  );
};

export default Login;
