import React, { useEffect, useState } from "react";
import axios from "axios";
import { 
   Snackbar, Alert 
} from "@mui/material";
import { useHistory } from "react-router-dom";
import { FaUserCircle, FaShoppingCart ,FaImages } from "react-icons/fa";
import "./UserDashboard.css";

const UserDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [artistDetails, setArtistDetails] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  

  const history = useHistory();
  const isArtworkInCart = (artworkId) => {
  return cartItems.some((item) => item.artwork.id === artworkId);
};
  

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:8080/api/artworks", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setArtworks(response.data);
      } catch (error) {
        console.error("Error fetching artworks:", error);
      }
    };

    fetchArtworks();
  }, []);

  const handleImageClick = (artwork) => {
    setSelectedArtwork(artwork);
    // Set artist details from artwork directly
    setArtistDetails({
      name: artwork.artistName || artwork.artist?.user?.username || "Unknown",
      rating: artwork.artistRating || artwork.artist?.rating || "N/A",
      specialization: artwork.artistSpecialization || artwork.artist?.specialization || "N/A",
    });
    setShowModal(true);
  };

  const handleAddToCart = async (artwork) => {
  try {
    const token = localStorage.getItem("token");
    await axios.post(
      "http://localhost:8080/api/cart/add",
      { artworkId: artwork.id },
      {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      }
    );
    alert("✅ Artwork added to cart!");
    history.push("/cart");

  } catch (error) {
    console.error("Error adding to cart:", error);
    const errorMessage = error?.response?.data;

    // ❌ Case 1: Buyer not found
    if (
      error?.response?.status === 403 ||
      (typeof errorMessage === "string" && errorMessage.includes("Buyer not found"))
    ) {
      alert("❌ Please create your buyer profile first.");
      history.push("/userprofile");
    }

    // ❌ Case 2: Already in cart
    else if (
      typeof errorMessage === "string" &&
      errorMessage.includes("already in your cart")
    ) {
      alert("⚠️ This artwork is already in your cart.");
    }

    // ❌ Case 3: Generic error
    else {
      alert("❌ Failed to add to cart. Please try again.");
    }
  }
};

  const filteredArtworks = artworks.filter(
    (art) =>
      art.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      art.artistName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      art.artist?.user?.username?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="page">
      {/* Profile and Cart Icons */}
      <div className="profile-cart-header">
  <div className="profile-cart-container">
    <div className="tooltip" style={{ marginLeft: "10px" }}>Purchased Art</div>
    <FaImages size={30} className="icon" onClick={() => history.push("/mypurchases")} />
    <div className="tooltip" style={{ marginLeft: "10px" }}>View Cart</div>
    <FaShoppingCart size={30} className="icon" onClick={() => history.push("/cart")} />
    <div className="tooltip">Fill your profile</div>
    <FaUserCircle size={32} className="icon" onClick={() => history.push("/userprofile")} />
  </div>

  <h1 className="explore-title">Explore Artworks</h1>
</div>

{/* Search Bar */}
<div style={{ marginBottom: "2rem" }}>
  <input
    type="text"
    placeholder="Search by title or artist"
    className="search"
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
  />
</div>

      {/* Artwork Grid */}
      <div className="art-grid">
        {filteredArtworks.map((artwork, index) => (
          <div
            key={artwork.id || index}
            style={{
              background: "white",
              color: "black",
              borderRadius: "10px",
              overflow: "hidden",
              boxShadow: "0 4px 15px gold",
              height: "200px",
              width: "300px",
              display: "flex",
              alignItems: "center",
              flexDirection: "column",
              justifyContent: "center",
            }}
          >
            <img
              src={artwork.imageUrl}
              alt={artwork.title}
              className="card-img"
              onClick={() => handleImageClick(artwork)}
              style={{ cursor: "pointer" }}
            />
          </div>
        ))}

        {filteredArtworks.length === 0 && (
          <p className="no-data">No artworks found.</p>
        )}
      </div>

      {/* Modal */}
      {showModal && selectedArtwork && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <img
              src={selectedArtwork.imageUrl}
              alt={selectedArtwork.title}
              className="modal-img"
            />
            <h2 className="modal-title">{selectedArtwork.title}</h2>
            <p className="modal-desc">{selectedArtwork.description}</p>
            <p className="modal-price">Price: ₹{selectedArtwork.price?.toFixed(2)}</p>

            {artistDetails && (
              <>
                <p><strong>Artist:</strong> {artistDetails.name}</p>
                {/* <p><strong>Specialization:</strong> {artistDetails.specialization}</p> */}
                <p><strong>Rating:</strong> {artistDetails.rating} ⭐</p>

              </>
            )}
            {/* <button className="btn" onClick={() => handleAddToCart(selectedArtwork)}>
              Add to Cart
            </button> */}
           {selectedArtwork?.status ? (
  selectedArtwork.status === "SOLD" ? (
    <p style={{ color: "red", fontWeight: "bold" }}>
      This artwork has already been sold.
    </p>
    ) : isLoading ? (
  <p>Loading...</p>
) : isArtworkInCart(selectedArtwork.id) ? (
  <button className="btn" disabled style={{ backgroundColor: "#ccc", cursor: "not-allowed" }}>
    Already in Cart
  </button>
  ) : (
    <button className="btn" onClick={() => handleAddToCart(selectedArtwork)}>
      Add to Cart
    </button>
  )
) : (
  <p style={{ color: "orange" }}>Loading status...</p>
)}

            <button className="close-btn" onClick={() => setShowModal(false)}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
