import React, { useState } from "react";
import { 
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, IconButton, Snackbar, Alert 
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import axios from "axios";
import { useHistory } from "react-router-dom";  // âœ… Correct for React Router v5

const Login = ({ open = false, onClose = () => {}, onLogin = () => {} }) => { 
  const history = useHistory();  // âœ… Correct usage
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [message, setMessage] = useState({ text: "", type: "error" });
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("ðŸ”µ Sending Data:", formData);
  
    try {
      const response = await axios.post("http://localhost:8080/api/users/login", formData, {
        headers: { "Content-Type": "application/json" },
        withCredentials: true,
      });
  
      console.log("ðŸŸ¢ Full Response:", response);
      console.log("ðŸŸ¢ Response Data:", response.data);
  
      const { token, role } = response.data || {};
  
      if (token && role) {  // âœ… Fix: Check both token & role
        localStorage.setItem("token", token);
        localStorage.setItem("role", role);
        localStorage.setItem("username", formData.username);
  
        onLogin(token);
        setMessage({ text: "âœ… Login successful! Redirecting...", type: "success" });
        setSnackbarOpen(true);
  
        setTimeout(() => {
          history.push(role === "artist" ? "/artist-dashboard" : "/dashboard");
        }, 1500);
      } else {
        console.error("âŒ API did not return token:", response.data);
        setMessage({ text: "âŒ Login failed. No token received.", type: "error" });
        setSnackbarOpen(true);
      }
    } catch (error) {
      console.error("ðŸ”´ Full API Error:", error);
  
      if (error.response) {
        setMessage({ text: error.response.data?.message || "âŒ Invalid username or password", type: "error" });
      } else if (error.request) {
        setMessage({ text: "âš ï¸ Server not responding. Try again later.", type: "error" });
      } else {
        setMessage({ text: "âŒ Request failed. Check network.", type: "error" });
      }
      setSnackbarOpen(true);
    }
  };
  
    
  // âœ… Handle Logout Function (Fixing 'handleLogout' not defined error)
  // const handleLogout = () => {
  //   localStorage.removeItem("token");
  //   localStorage.removeItem("role");
  //   localStorage.removeItem("username");
  //   history.push("/login");
  // };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>
        Login
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{ position: "absolute", right: 8, top: 8 }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <TextField 
          label="Username" 
          name="username" 
          type="text" 
          fullWidth 
          margin="dense" 
          value={formData.username} 
          onChange={handleChange} 
          required
        />
        <TextField 
          label="Password" 
          name="password" 
          type="password" 
          fullWidth 
          margin="dense" 
          value={formData.password} 
          onChange={handleChange} 
          required
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary" variant="contained">Login</Button>
      </DialogActions>

      {/* Snackbar for Feedback */}
      <Snackbar open={snackbarOpen} autoHideDuration={3000} onClose={() => setSnackbarOpen(false)}>
        <Alert onClose={() => setSnackbarOpen(false)} severity={message.type} sx={{ width: '100%' }}>
          {message.text}
        </Alert>
      </Snackbar>
    </Dialog>
  );
};

export default Login;
