import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useHistory } from "react-router-dom";
import {
  CardElement,
  useStripe,
  useElements,
  Elements,
} from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";

// Replace with your own public key
const stripePromise = loadStripe("pk_test_XXXXXXXXXXXXXXXXXXXXXXXX");

const OrderDetailsComponent = () => {
  const { orderId } = useParams();
  const history = useHistory();
  const stripe = useStripe();
  const elements = useElements();

  const [order, setOrder] = useState(null);
  const [clientSecret, setClientSecret] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [successMsg, setSuccessMsg] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          setErrorMsg("âš ï¸ You must be logged in to view this page.");
          return;
        }

        // 1. Fetch order
        const response = await axios.get(
          `http://localhost:8080/api/payments/${orderId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setOrder(response.data);

        // 2. Create payment intent
        const intentRes = await axios.post(
          "http://localhost:8080/api/payments/create-payment-intent",
          {
            orderId: response.data.orderId,
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
          }
        );
        setClientSecret(intentRes.data.clientSecret);
      } catch (error) {
        console.error("âŒ Error:", error);
        setErrorMsg("âŒ Failed to fetch order or create payment intent.");
      }
    };

    if (orderId) {
      fetchOrder();
    }
  }, [orderId]);

  const handlePayment = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (!stripe || !elements) {
      return;
    }

    const cardElement = elements.getElement(CardElement);
    const { error, paymentIntent } = await stripe.confirmCardPayment(
      clientSecret,
      {
        payment_method: {
          card: cardElement,
          billing_details: {
            name: "Customer",
          },
        },
      }
    );

    if (error) {
      console.error("Payment Error:", error);
      setErrorMsg(error.message);
      setLoading(false);
    } else if (paymentIntent.status === "succeeded") {
      setSuccessMsg("ðŸŽ‰ Payment successful!");
      setErrorMsg("");
    }
    setLoading(false);
  };

  if (errorMsg) {
    return (
      <div style={{ textAlign: "center", marginTop: "50px", color: "red" }}>
        <h2>{errorMsg}</h2>
        <button onClick={() => history.push("/")} style={{ marginTop: "20px" }}>
          Go Back
        </button>
      </div>
    );
  }

  if (!order) {
    return (
      <h2 style={{ textAlign: "center", marginTop: "50px" }}>
        Loading order details...
      </h2>
    );
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>Order Details</h2>
      <div
        style={{
          width: "300px",
          padding: "15px",
          borderRadius: "10px",
          backgroundColor: "#f0f0f0",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        <img
          src={order.imageUrl}
          alt={order.artworkTitle}
          style={{
            width: "100%",
            height: "200px",
            objectFit: "cover",
            borderRadius: "8px",
          }}
        />
        <h3>{order.artworkTitle}</h3>
        <p>
          <strong>Artist:</strong> {order.artistName}
        </p>
        <p>
          <strong>Price:</strong> â‚¹{order.price}
        </p>
        <p>
          <strong>Status:</strong>{" "}
          <span
            style={{
              color:
                order.status === "COMPLETED"
                  ? "green"
                  : order.status === "PENDING"
                  ? "orange"
                  : "red",
              fontWeight: "bold",
            }}
          >
            {order.status}
          </span>
        </p>

        <form onSubmit={handlePayment} style={{ marginTop: "20px" }}>
          <CardElement />
          <button
            type="submit"
            disabled={!stripe || !clientSecret || loading}
            style={{
              marginTop: "15px",
              padding: "10px",
              backgroundColor: "#007bff",
              color: "#fff",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              width: "100%",
            }}
          >
            {loading ? "Processing..." : "Pay Now"}
          </button>
        </form>

        {successMsg && (
          <p style={{ color: "green", marginTop: "10px" }}>{successMsg}</p>
        )}
      </div>
    </div>
  );
};

// Wrap the component with <Elements> and Stripe
const OrderDetails = () => (
  <Elements stripe={stripePromise}>
    <OrderDetailsComponent />
  </Elements>
);

export default OrderDetails;
