import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom"; // v5 uses useHistory
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "axios";

const OrderDetails = () => {
  const { orderId } = useParams();
  const history = useHistory(); // v5 navigation

  const stripe = useStripe();
  const elements = useElements();

  const [order, setOrder] = useState(null);
  const [clientSecret, setClientSecret] = useState("");
  const [loading, setLoading] = useState(true);
  const [paymentStatus, setPaymentStatus] = useState("");
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchOrderAndClientSecret = async () => {
      try {
        const orderRes = await axios.get(`http://localhost:8080/api/payments/${orderId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrder(orderRes.data);

        const intentRes = await axios.post(
          "http://localhost:8080/api/payments/create-payment-intent",
          { orderId },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setClientSecret(intentRes.data.clientSecret);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching order or creating intent:", err);

        if (err.response?.status === 403) {
          setError("You are not authorized to view or pay for this order.");
          setTimeout(() => {
            history.push("/dashboard"); // v5 redirect
          }, 3000);
        } else if (err.response?.status === 500) {
          setError("No payment record found for this order yet.");
        } else {
          setError("Failed to load order or create payment intent.");
        }

        setLoading(false);
      }
    };

    fetchOrderAndClientSecret();
  }, [orderId, token, history]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) return;

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (result.error) {
      setError(result.error.message);
    } else {
      if (result.paymentIntent.status === "succeeded") {
        await axios.post(
          "/api/payments/confirm",
          {
            transactionId: result.paymentIntent.id,
            status: "SUCCEEDED",
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setPaymentStatus("Payment successful!");
      } else {
        setPaymentStatus("Payment not completed.");
      }
    }
  };

  if (loading) return <p className="text-white text-center mt-10">Loading...</p>;
  if (error)
    return (
      <p className="text-red-500 text-center mt-10">
        {error}
        <br />
        Redirecting...
      </p>
    );

  return (
    <div className="max-w-lg mx-auto mt-10 bg-white p-6 rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center">Order Payment</h2>
      <div className="mb-4">
        <p>
          <strong>Artwork:</strong> {order.artwork.title}
        </p>
        <p>
          <strong>Artist:</strong> {order.artwork.artistName}
        </p>
        <p>
          <strong>Price:</strong> â‚¹{order.totalAmount}
        </p>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-4 p-4 border rounded">
          <CardElement />
        </div>
        <button
          type="submit"
          disabled={!stripe || !elements}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Pay Now
        </button>
      </form>

      {paymentStatus && (
        <p className="mt-4 text-green-600">
          {paymentStatus}{" "}
          <span role="img" aria-label="celebration">
            ðŸŽ‰
          </span>
        </p>
      )}
    </div>
  );
};

export default OrderDetails;
