import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Category = () => {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");

    axios.get("http://localhost:8080/api/admin/dashboard/artworks", {
      headers: {
        'Authorization': `Bearer ${token}`,
      }
    })
    .then(response => {
      setArtworks(response.data);
    })
    .catch(error => {
      console.error('Error fetching artworks:', error);
    });
  }, []);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '20px', fontSize: '22px', fontWeight: 'bold', color: '#d63384' }}>
        Artwork Categories
      </h2>

      <table
        style={{
          width: '100%',
          borderCollapse: 'collapse',
          backgroundColor: '#ffe6f0',
          border: '3px solid #f8a5c2',
        }}
      >
        <thead style={{ backgroundColor: '#f8a5c2', color: '#fff' }}>
          <tr>
            <th align="left" style={{ padding: '10px' }}>Title</th>
            <th align="left" style={{ padding: '10px' }}>Category</th>
          </tr>
        </thead>
        <tbody>
          {artworks.map((art, index) => (
            <tr
              key={art.id}
              style={{
                backgroundColor: index % 2 === 0 ? '#ffd6e8' : '#ffe6f0',
              }}
            >
              <td style={{ padding: '10px', borderBottom: '2px solid #f8a5c2' }}>{art.title}</td>
              <td style={{ padding: '10px', borderBottom: '2px solid #f8a5c2' }}>{art.category}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Category;
