import React, { useState } from "react";
import axios from "axios";
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Snackbar } from "@mui/material";

const Login = ({ open, onClose, onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState(null);
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null); // Clear previous messages

    try {
      console.log("üîµ Sending Data:", { username, password });

      const response = await axios.post("http://localhost:8080/api/login", {
        username,
        password,
      });

      console.log("üü¢ Full Response:", response);
      console.log("üü¢ Response Data:", response.data);

      if (response.data.token) {
        console.log("üîÄ Redirecting based on role:", response.data.role);
        onLogin(response.data.token, response.data.role); // Calls handleLogin from Header.js
      } else {
        console.error("‚ùå API did not return token. Response Data:", response.data);
        setMessage({ text: "‚ùå Login failed. No token received.", type: "error" });
        setSnackbarOpen(true);
      }
    } catch (error) {
      console.error("üî¥ Full API Error:", error);

      if (error.response) {
        console.log("üî¥ Error Response Data:", error.response.data);
        setMessage({ text: error.response.data?.message || "‚ùå Invalid username or password", type: "error" });
      } else if (error.request) {
        setMessage({ text: "‚ö†Ô∏è Server not responding. Try again later.", type: "error" });
      } else {
        setMessage({ text: "‚ùå Request failed. Check network.", type: "error" });
      }
      setSnackbarOpen(true);
    }
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Login</DialogTitle>
      <DialogContent>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Username"
            variant="outlined"
            fullWidth
            margin="normal"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <TextField
            label="Password"
            type="password"
            variant="outlined"
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Login
          </Button>
        </form>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">Cancel</Button>
      </DialogActions>

      {/* Snackbar for error messages */}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={4000}
        onClose={handleCloseSnackbar}
        message={message?.text}
      />
    </Dialog>
  );
};

export default Login;
