import React, { useState } from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "axios";

const CheckoutForm = ({ artworkId, clientSecret }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [downloadUrl, setDownloadUrl] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [loading, setLoading] = useState(false); // New loading state

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements || !clientSecret) return;

    setLoading(true); // Set loading state

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    setLoading(false); // Reset loading state

    if (result.error) {
      console.error("❌ Payment failed:", result.error.message);
      setErrorMessage(result.error.message);
    } else {
      if (result.paymentIntent.status === "succeeded") {
        console.log("✅ Payment successful!");
        setPaymentSuccess(true); // Show download button
      }
    }
  };

  const fetchDownloadUrl = async () => {
    try {
      const res = await axios.get(`http://localhost:8080/api/artworks/download/${artworkId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (res.data.downloadUrl) {
        window.open(res.data.downloadUrl, "_blank"); // open image in new tab
      } else {
        setErrorMessage("Download URL not available.");
      }
    } catch (error) {
      console.error("Error fetching download URL", error);
      setErrorMessage("Failed to fetch download link.");
    }
  };

  // 
  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
      <div className="bg-white shadow-2xl rounded-2xl p-8 w-full max-w-md">
        <h2 className="text-3xl font-extrabold text-center text-gray-800 mb-6">Order Payment</h2>
  
        <div className="bg-gray-50 p-4 rounded-lg mb-6">
          <p className="text-gray-700"><strong>Transaction ID:</strong> {payment.transactionId}</p>
          <p className="text-gray-700"><strong>Amount:</strong> ₹{payment.amount}</p>
        </div>
  
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="p-4 border border-gray-300 rounded-lg shadow-sm bg-white">
            <CardElement />
          </div>
          <button
            type="submit"
            disabled={!stripe || !elements}
            className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300"
          >
            Pay Now
          </button>
        </form>
  
        {paymentStatus && (
          <p className="mt-4 text-center text-green-600 font-semibold">{paymentStatus}</p>
        )}
  
        {paymentStatus === "Payment successful!" && payment?.downloadUrl && (
          <div className="mt-6 text-center">
            <a
              href={payment.downloadUrl}
              download
              className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300"
            >
              Download Artwork
            </a>
          </div>
        )}
      </div>
    </div>
  
  );
};

export default CheckoutForm;
