import React from 'react';
import { Link } from 'react-router-dom';
import { 
  BsCart3, BsGrid1X2Fill, BsFillArchiveFill, BsFillGrid3X3GapFill, 
  BsPeopleFill, BsListCheck, BsMenuButtonWideFill, BsFillGearFill 
} from 'react-icons/bs';
const Sidebar = () => {
  const [showLogout, setShowLogout] = useState(false);
  const history = useHistory();

  const handleLogout = () => {
    localStorage.removeItem('token');
    history.push('/login');
  };

function Sidebar({ openSidebarToggle, OpenSidebar }) {
  return (
    <aside id="sidebar" className={openSidebarToggle ? "sidebar-responsive" : ""}>
      <div className='sidebar-title'>
        <div className='sidebar-brand'>
          <BsCart3 className='icon_header' /> art fusion 
        </div>
        <span className='icon close_icon' onClick={OpenSidebar}>X</span>
      </div>

      <ul className='sidebar-list'>
        <li className='sidebar-list-item'>
          <Link to="/admin-dashboard">
            <BsGrid1X2Fill className='icon' /> Dashboard
          </Link>
        </li>
        <li className='sidebar-list-item'>
          <Link to="/admin-dashboard/artworks">
            <BsFillArchiveFill className='icon' /> Artwork
          </Link>
        </li>
        <li className='sidebar-list-item'>
          <Link to="/admin-dashboard/themes">
            <BsFillGrid3X3GapFill className='icon' /> Themes
          </Link>
        </li>
        <li className='sidebar-list-item'>
          <Link to="/admin-dashboard/customer">
            <BsPeopleFill className='icon' /> Customers
          </Link>
        </li>
        <li className='sidebar-list-item'>
          <Link to="/admin-dashboard/order-status">
            <BsListCheck className='icon' /> Order Status
          </Link>
        </li>
        <li className='sidebar-list-item'>
          <Link to="/admin/reports">
            <BsMenuButtonWideFill className='icon' /> Reports
          </Link>
        </li>
        <li
        className="sidebar-list-item"
        onClick={() => setShowLogout(!showLogout)}
        style={{ cursor: "pointer" }}
      >
        <BsFillGearFill className="icon" /> Settings
      </li>

      {showLogout && (
        <p
          style={{ margin: "5px 20px", cursor: "pointer", color: "black" }}
          onClick={handleLogout}
        >
          Logout
        </p>
      )}
      </ul>
    </aside>
  );
}
}

export default Sidebar;
