import React, { useEffect, useState } from "react";
import axios from "axios";

const OrderDetails = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/payments/${orderId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setOrders(response.data);
      } catch (error) {
        console.error("Failed to fetch order history:", error);
      }
    };

    fetchOrders();
  }, []);

  if (orders.length === 0) {
    return <h2 style={{ textAlign: "center", marginTop: "50px" }}>No orders found.</h2>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>Order History</h2>
      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {orders.map((order, index) => (
          <div
            key={index}
            style={{
              width: "280px",
              padding: "15px",
              borderRadius: "10px",
              backgroundColor: "#f0f0f0",
              boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            }}
          >
            <img
              src={order.imageUrl}
              alt={order.artworkTitle}
              style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "8px" }}
            />
            <h3>{order.artworkTitle}</h3>
            <p><strong>Artist:</strong> {order.artistName}</p>
            <p><strong>Price:</strong> â‚¹{order.price}</p>
            <p><strong>Date:</strong> {new Date(order.orderDate).toLocaleDateString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrderDetails;
