import React, { useState } from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "axios";

const CheckoutForm = ({ artworkId, clientSecret }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) return;

    try {
      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
        },
      });

      if (result.error) {
        console.error("‚ùå Payment failed:", result.error.message);
        setErrorMessage(result.error.message);
        return;
      }

      if (result.paymentIntent.status === "succeeded") {
        alert("‚úÖ Payment successful!");
        setPaymentSuccess(true);
      }
    } catch (error) {
      console.error("‚ùå Payment error:", error);
      setErrorMessage("Payment processing failed.");
    }
  };

  const handleDownloadClick = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/artworks/download/${artworkId}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`, // if using token
          },
        }
      );

      if (response.status === 200 && response.data.downloadUrl) {
        const downloadUrl = response.data.downloadUrl;
        window.open(downloadUrl, "_blank");
      } else {
        setErrorMessage("You are not eligible to download this artwork.");
      }
    } catch (error) {
      console.error("‚ùå Error fetching download link:", error);
      setErrorMessage("An error occurred while retrieving download link.");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} style={{ marginTop: "30px" }}>
        <CardElement />
        <button type="submit" disabled={!stripe} style={{ marginTop: "20px" }}>
          Pay Now
        </button>
      </form>

      {/* ‚úÖ Show download button only after payment */}
      {paymentSuccess && (
        <div>
          <h3>üéâ Payment completed!</h3>
          <button
            onClick={handleDownloadClick}
            style={{ marginTop: "20px" }}
          >
            Download Artwork
          </button>
        </div>
      )}

      {errorMessage && (
        <div style={{ color: "red", marginTop: "20px" }}>
          <p>{errorMessage}</p>
        </div>
      )}
    </div>
  );
};

export default CheckoutForm;
