// export default ArtistDashboard;
import React, { useEffect, useState } from "react";
import axios from "axios";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [isEditMode, setIsEditMode] = useState(false);
  // Include price in the editData state for updating
  const [editData, setEditData] = useState({ title: "", description: "", price: "" });
  const [formData, setFormData] = useState({
    title: "",
    category: "",
    description: "",
    imageUrl: "",
    price: "",
  });

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }
      const response = await axios.get("http://localhost:8080/api/artworks/my-artworks", {
        headers: { Authorization: `Bearer ${token}` },

      });
      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }
      await axios.post("http://localhost:8080/api/artworks/upload", formData, {
        headers:{Authorization: `Bearer ${token}` },
          "Content-Type": "application/json",
      });
      alert("‚úÖ Artwork uploaded successfully!");
      setIsDialogOpen(false);
      fetchArtworks(); // Refresh artworks list
    } catch (error) {
      console.error("üî¥ Error uploading artwork:", error);
    }
  };

  const handleDeleteArtwork = async () => {
    if (!selectedArtwork) return;
    const confirmDelete = window.confirm("Are you sure you want to delete this artwork?");
    if (!confirmDelete) return;
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }
      await axios.delete(`http://localhost:8080/api/artworks/${selectedArtwork.id}`, { // ‚úÖ Added `,` before `{`
        headers: { Authorization: `Bearer ${token}` }
      });
      
      alert("‚úÖ Artwork deleted successfully!");
      setArtworks((prev) => prev.filter((art) => art.id !== selectedArtwork.id));
      setSelectedArtwork(null);
    } catch (error) {
      console.error("üî¥ Error deleting artwork:", error);
    }
  };

  const handleEditInputChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  const handleUpdateArtwork = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }
      const response = await axios.put(
        `http://localhost:8080/api/artworks/${selectedArtwork.id}`,

        editData, // This should include the updated title, description, price, etc.
        {
          headers: {
            headers: { Authorization: `Bearer ${token}` }, // Ensure token has the "ARTIST" authority
            "Content-Type": "application/json",
          },
        }
      );
      alert("‚úÖ Artwork updated successfully!");
      // Update the local state with the updated artwork data from response.data
      setSelectedArtwork(response.data);
      setArtworks((prev) =>
        prev.map((art) => (art.id === selectedArtwork.id ? response.data : art))
      );
      setIsEditMode(false);
    } catch (error) {
      console.error("üî¥ Error updating artwork:", error);
    }
  };

  return (
    <div
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/midnight_blue_wall_bg.png)`, 

        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        padding: "20px",
        opacity: 0.9,
      }}
    >
      {/* Header with Title on left and Sell Your Art button + Profile Icon on right */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <h1 style={{ color: "white", fontSize: "28px", fontWeight: "bold" }}>
          Welcome to Art Gallery!
        </h1>
        <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          {/* Sell Your Art Button */}
          <button
            onClick={() => setIsDialogOpen(true)}
            style={{
              backgroundColor: "#007bff",
              color: "white",
              padding: "10px 15px",
              borderRadius: "5px",
              border: "none",
              cursor: "pointer",
            }}
          >
            Sell Your Art
          </button>
          {/* Profile Icon */}
          <div style={{ position: "relative" }}>
            <button
              style={{
                backgroundColor: "transparent",
                border: "none",
                cursor: "pointer",
                fontSize: "20px",
              }}
              onClick={() => setShowProfileDropdown(!showProfileDropdown)}
            >
              <span role="img" aria-label="Profile">
                üßë
              </span>
            </button>
            {showProfileDropdown && (
              <div
                style={{
                  position: "absolute",
                  top: "40px",
                  right: "0",
                  background: "white",
                  boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                  borderRadius: "5px",
                  padding: "10px",
                  width: "120px",
                  textAlign: "center",
                }}
              >
                <p style={{ margin: "5px 0", cursor: "pointer" }}>Profile</p>
                <p style={{ margin: "5px 0", cursor: "pointer" }}>Logout</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Artworks Grid */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          flexWrap: "wrap",
          gap: "20px",
          marginTop: "20px",
        }}
      >
        {artworks.map((art, index) => (
          <div
            key={art.id}
            style={{
              position: "relative",
              width: index === artworks.length - 1 ? "280px" : "300px",
              borderRadius: "10px",
              overflow: "hidden",
              cursor: "pointer",
            }}
            onClick={() => {
              setSelectedArtwork(art);
              setIsEditMode(false);
            }}
          >
            <img
              src={art.imageUrl}
              alt={art.title}
              style={{
                width: "90%",
                height: "200px",
                objectFit: "cover",
                borderRadius: "10px",
                boxShadow: "0px 4px 15px rgba(255, 215, 0, 0.8)",
                border: "2px solid gold",
              }}
            />
          </div>
        ))}
      </div>

      {/* Sell Your Art Form Modal */}
      {isDialogOpen && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "400px",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
            zIndex: 1000,
          }}
        >
          <h2>Sell Your Art</h2>
          <form
            onSubmit={handleSubmit}
            style={{ display: "flex", flexDirection: "column", gap: "10px" }}
          >
            <input
              type="text"
              name="title"
              placeholder="Title"
              value={formData.title}
              onChange={handleInputChange}
              required
              style={{
                padding: "8px",
                borderRadius: "5px",
                border: "1px solid #ddd",
              }}
            />
            <input
              type="text"
              name="category"
              placeholder="Category"
              value={formData.category}
              onChange={handleInputChange}
              required
              style={{
                padding: "8px",
                borderRadius: "5px",
                border: "1px solid #ddd",
              }}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={formData.description}
              onChange={handleInputChange}
              required
              style={{
                padding: "8px",
                borderRadius: "5px",
                border: "1px solid #ddd",
              }}
            />
            <input
              type="text"
              name="imageUrl"
              placeholder="Image URL"
              value={formData.imageUrl}
              onChange={handleInputChange}
              required
              style={{
                padding: "8px",
                borderRadius: "5px",
                border: "1px solid #ddd",
              }}
            />
            <input
              type="number"
              name="price"
              placeholder="Price"
              value={formData.price}
              onChange={handleInputChange}
              required
              style={{
                padding: "8px",
                borderRadius: "5px",
                border: "1px solid #ddd",
              }}
            />
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <button
                type="submit"
                style={{
                  background: "#28a745",
                  color: "white",
                  padding: "8px",
                  borderRadius: "5px",
                  border: "none",
                  cursor: "pointer",
                }}
              >
                Upload
              </button>
              <button
                type="button"
                onClick={() => setIsDialogOpen(false)}
                style={{
                  background: "#dc3545",
                  color: "white",
                  padding: "8px",
                  borderRadius: "5px",
                  border: "none",
                  cursor: "pointer",
                }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Artwork Details Modal with Delete and Update Buttons */}
      {selectedArtwork && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "400px",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
            zIndex: 1000,
          }}
        >
          {isEditMode ? (
            <form
              onSubmit={handleUpdateArtwork}
              style={{ display: "flex", flexDirection: "column", gap: "10px" }}
            >
              <input
                type="text"
                name="title"
                value={editData.title}
                onChange={handleEditInputChange}
                required
                style={{
                  padding: "8px",
                  borderRadius: "5px",
                  border: "1px solid #ddd",
                }}
              />
              <textarea
                name="description"
                value={editData.description}
                onChange={handleEditInputChange}
                required
                style={{
                  padding: "8px",
                  borderRadius: "5px",
                  border: "1px solid #ddd",
                }}
              />
              <input
                type="number"
                name="price"
                value={editData.price}
                onChange={handleEditInputChange}
                required
                style={{
                  padding: "8px",
                  borderRadius: "5px",
                  border: "1px solid #ddd",
                }}
              />
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <button
                  type="submit"
                  style={{
                    background: "#28a745",
                    color: "white",
                    padding: "8px",
                    borderRadius: "5px",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Save
                </button>
                <button
                  type="button"
                  onClick={() => setIsEditMode(false)}
                  style={{
                    background: "#dc3545",
                    color: "white",
                    padding: "8px",
                    borderRadius: "5px",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          ) : (
            <>
              <h3>{selectedArtwork.title}</h3>
              <p>
                <strong>Category:</strong> {selectedArtwork.category}
              </p>
              <p>
                <strong>Description:</strong> {selectedArtwork.description}
              </p>
              <p style={{ fontWeight: "bold", fontSize: "18px" }}>
                ‚Çπ{selectedArtwork.price}
              </p>
              <div style={{ display: "flex", justifyContent: "space-between", marginTop: "10px" }}>
                <button
                  onClick={() => setSelectedArtwork(null)}
                  style={{
                    background: "#dc3545",
                    color: "white",
                    padding: "8px",
                    borderRadius: "5px",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Close
                </button>
                <button
                  onClick={handleDeleteArtwork}
                  style={{
                    background: "red",
                    color: "white",
                    padding: "8px",
                    borderRadius: "5px",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Delete
                </button>
                <button
                  onClick={() => {
                    setIsEditMode(true);
                    setEditData({
                      title: selectedArtwork.title,
                      description: selectedArtwork.description,
                      price: selectedArtwork.price,
                    });
                  }}
                  style={{
                    background: "blue",
                    color: "white",
                    padding: "8px",
                    borderRadius: "5px",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Update
                </button>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
