import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminBuyerList = () => {
  const [users, setUsers] = useState([]);
  const token = localStorage.getItem('token'); // or however you're storing it

  useEffect(() => {
    axios.get("http://localhost:8080/api/admin/dashboard/users", {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(response => setUsers(response.data))
    .catch(error => console.error('Error fetching users:', error));
  }, [token]);

  const handleDelete = (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      axios.delete(`http://localhost:8080/api/admin/dashboard/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
      .then(() => {
        setUsers(prev => prev.filter(user => user.id !== userId));
      })
      .catch(error => console.error('Error deleting user:', error));
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Customers</h2>
      <table style={{ border: '3px solid black', borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th style={{ border: '3px solid white', padding: '10px' }}>Username</th>
            <th style={{ border: '3px solid white', padding: '10px'  }}>Role</th>
            <th style={{ border: '3px solid white', padding: '10px' }}>Last Login</th>
            <th style={{ border: '3px solid white', padding: '10px'}}>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td style={{ border: '3px solid white', padding: '8px' }}>{user.username}</td>
              <td style={{ border: '3px solid white', padding: '8px' }}>{user.role}</td>
              <td style={{ border: '3px solid white', padding: '8px' }}>{user.lastLogin || 'N/A'}</td>
              <td style={{ border: '3px solid white', padding: '8px' }}>
                <button 
                  onClick={() => handleDelete(user.id)} 
                  style={{
                    backgroundColor: '#e74c3c',
                    color: 'white',
                    border: 'none',
                    padding: '6px 12px',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminBuyerList;
