import React, { useEffect, useState } from "react";
import axios from "axios";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
 // const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedArtwork, setSelectedArtwork] = useState(null); // State for selected artwork

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const response = await axios.get("http://localhost:8080/api/artworks/my-artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  return (
    <div
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/midnight_blue_wall_bg.png)`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        padding: "20px",
        opacity: 0.9,
      }}
    >
      <h1 style={{ color: "white", fontSize: "28px", fontWeight: "bold" }}>
        Welcome to Art Gallery!
      </h1>

      {/* Artworks Grid */}
      <div
        style={{
        display: "flex",
        justifyContent: "space-between", // Ensures spacing
        alignItems: "center",
        flexWrap: "wrap",
        gap: "20px",
        marginTop: "20px",
      }}
    >
        {artworks.map((art,index) => (
          <div
            key={art.id}
            style={{
              position: "relative",
              width: index === artworks.length - 1 ? "280px" : "300px",
              borderRadius: "10px",
              overflow: "hidden",
              cursor: "pointer",
              boxShadow: "0px 4px 12px rgba(0,0,0,0.2)",
            }}
            onClick={() => setSelectedArtwork(art)}
          >
            {/* Artwork Image */}
            <img
              src={art.imageUrl}
              alt={art.title}
              style={{
                width: "100%",
                height: "250px",
                objectFit: "cover",
                borderRadius: "10px",
              }}
            />
          </div>
        ))}
      </div>

      {/* Pop-up Dialog for Artwork Details */}
      {selectedArtwork && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "400px",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
            zIndex: 1000,
          }}
        >
          <h3>{selectedArtwork.title}</h3>
          <p><strong>Category:</strong> {selectedArtwork.category}</p>
          <p><strong>Description:</strong> {selectedArtwork.description}</p>
          <p style={{ fontWeight: "bold", fontSize: "18px" }}>‚Çπ{selectedArtwork.price}</p>

          <button
            onClick={() => setSelectedArtwork(null)}
            style={{
              background: "#dc3545",
              color: "white",
              padding: "8px",
              borderRadius: "5px",
              border: "none",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
