import React, { useState } from "react";
import { NavLink } from "react-router-dom"; // âœ… Import NavLink for navigation
import Register from "./auth/Register";
import Login from "./auth/Login";
import "./Header.css";

const Header = () => {
  const [openRegister, setOpenRegister] = useState(false);
  const [openLogin, setOpenLogin] = useState(false);
  
  // âœ… Retrieve values from localStorage
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem("token"));
  const [userRole, setUserRole] = useState(localStorage.getItem("role") || null);

  const handleLogin = (token, role) => {
    console.log("âœ… User logged in with token:", token);
    localStorage.setItem("token", token);
    localStorage.setItem("role", role.toLowerCase()); // ðŸ”¹ Store role in lowercase for consistency
    setIsAuthenticated(true);
    setUserRole(role.toLowerCase());
  };

  // âœ… Logout function
  const handleLogout = () => {
    localStorage.clear();
    setIsAuthenticated(false);
    setUserRole(null);
  };

  return (
    <header className="tinted-header">
      <h1 className="header-title">Welcome To ArtFusion</h1>
      <div className="button-container">
        {isAuthenticated ? (
          <>
            {/* âœ… NavLink for Navigation Instead of history.push */}
            <NavLink 
              to={userRole === "artist" ? "/artist-dashboard" : "/dashboard"} 
              className="auth-button"
              activeClassName="active-link"
            >
              Dashboard
            </NavLink>

            <button className="auth-button logout" onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <>
            <button className="auth-button" onClick={() => setOpenRegister(true)}>
              Register
            </button>
            <button className="auth-button" onClick={() => setOpenLogin(true)}>
              Login
            </button>
          </>
        )}
      </div>

      {/* Signup & Login Dialogs */}
      <Register open={openRegister} onClose={() => setOpenRegister(false)} />
      <Login open={openLogin} onClose={() => setOpenLogin(false)} onLogin={handleLogin} />
    </header>
  );
};

export default Header;
