import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  BsFillArchiveFill, BsFillGrid3X3GapFill, BsPeopleFill, BsFillBellFill
} from 'react-icons/bs';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  ResponsiveContainer, LineChart, Line
} from 'recharts';

function Home() {
  const [stats, setStats] = useState({});
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');

    // Fetch stats
    axios.get('http://localhost:8080/api/admin/dashboard/stats', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => setStats(response.data))
    .catch(error => console.error('Error fetching stats:', error));

    // Fetch orders
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/admin/dashboard/orders', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, []);

  // Bar Chart Data
  const chartData = [
    { name: 'Completed Orders', value: stats.totalCompletedOrders || 0 },
    { name: 'Failed Orders', value: stats.totalFailedOrders || 0 },
    { name: "Today's Orders", value: stats.artworksSoldToday || 0 }
  ];

  // Line Chart Data (earnings by date)
  const earningsData = {};

  orders.forEach(order => {
    if (order.orderStatus === 'COMPLETED') {
      const date = order.orderDate; // format: "YYYY-MM-DD"
      if (!earningsData[date]) {
        earningsData[date] = 0;
      }
      earningsData[date] += order.totalPrice;
    }
  });

  const lineChartData = Object.keys(earningsData).map(date => ({
    date,
    earnings: earningsData[date]
  }));

  return (
    <main className="main-container">
      <div className="main-title">
        <h3>REPORT</h3>
      </div>

      <div className="main-cards">
        <div className="card">
          <div className="card-inner">
            <h3>Artworks Uploaded Today</h3>
            <BsFillArchiveFill className="card_icon" />
          </div>
          <h1>{stats.artworksUploadedToday || 0}</h1>
        </div>

        <div className="card">
          <div className="card-inner">
            <h3>Users Registered Today</h3>
            <BsFillGrid3X3GapFill className="card_icon" />
          </div>
          <h1>{stats.usersRegisteredToday || 0}</h1>
        </div>

        <div className="card">
          <div className="card-inner">
            <h3>Orders Today</h3>
            <BsPeopleFill className="card_icon" />
          </div>
          <h1>{stats.artworksSoldToday || 0}</h1>
        </div>

        <div className="card">
          <div className="card-inner">
            <h3>Failed Orders</h3>
            <BsFillBellFill className="card_icon" />
          </div>
          <h1>{stats.totalFailedOrders || 0}</h1>
        </div>
      </div>

      <div className="charts" style={{ display: 'flex', gap: '40px', flexWrap: 'wrap' }}>
        {/* Bar Chart */}
        <div style={{ flex: 1, minWidth: '300px', height: 300 }}>
          <ResponsiveContainer width="100%" height="100%">
            <BarChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="value" fill="#FFC107" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Line Chart */}
        <div style={{ flex: 1, minWidth: '300px', height: 300 }}>
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={lineChartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="earnings" stroke="#00C49F" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>
    </main>
  );
}

export default Home;
