import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";  // Use useHistory instead of useNavigate
import { getUserProfile, updateUserProfile } from "../services/authService";

const Profile = () => {
  const [user, setUser] = useState({});
  const [name, setName] = useState("");
  const history = useHistory();  // useHistory for navigation
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      history.push("/login");  // Redirect to login if no token
      return;
    }

    getUserProfile(token)
      .then((res) => {
        setUser(res.data);
        setName(res.data.name);
      })
      .catch(() => history.push("/login"));  // Redirect on error
  }, [history, token]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    await updateUserProfile(token, { name });
    alert("Profile updated successfully");
  };

  return (
    <div>
      <h2>Edit Profile</h2>
      <form onSubmit={handleUpdate}>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default Profile;
