import ArtistDashboard from "./components/board/ArtistDashboard";
import Login from "./components/auth/Login"; // ✅ Ensure Login component is imported

function App() {
  const userRole = (localStorage.getItem("role") || "").toLowerCase(); // ✅ Convert to lowercase

  return (
    <Router>
      <div className="app-container">
        <BackgroundVideo />
        <Header />

        <Switch>
          <Route exact path="/">
            <Redirect to="/artist-dashboard" />
          </Route>
          <Route exact path="/artist-dashboard" component={ArtistDashboard} />
