import React, { useEffect, useState } from "react";
import axios from "axios";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const response = await axios.get("http://localhost:8080/api/artworks", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  return (
    <div style={{ background: "white", minHeight: "100vh", padding: "20px" }}>
      <h1>Art Gallery</h1>
      {artworks.length === 0 ? (
        <p>Loading artworks...</p>
      ) : (
        <div>
          {artworks.map((art) => (
            <div key={art.id}>
              <h3>{art.title}</h3>
              <img src={art.imageUrl} alt={art.title} width="200px" />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
