import React, { useEffect, useState } from "react";
import axios from "axios";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [showProfileDropdown] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [formData, setFormData] = useState({
    title: "",
    category: "",
    description: "",
    imageUrl: "",
    price: "",
  });

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const response = await axios.get("http://localhost:8080/api/artworks/my-artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }
  await axios.post("http://localhost:8080/api/artworks/upload", formData, {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "multipart/form-data", // ‚úÖ Correct content type
    },
    withCredentials: true // ‚úÖ This ensures the token/cookie is sent
  });
  alert("‚úÖ Artwork uploaded successfully!");
      setIsDialogOpen(false);
      fetchArtworks();
    } catch (error) {
      console.error("üî¥ Error uploading artwork:", error);
    }
  };
      

  return (
    <div
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/light.jpeg)`  ,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        padding: "20px",
        opacity: 0.9
      }}
    >
    
  

      {/* Header Section */}
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
          gap: "15px",
          padding: "10px",
        }}
      >
        {/* Sell Your Art Button */}
        <button
          style={{
            backgroundColor: "#007bff",
            color: "white",
            padding: "10px 15px",
            borderRadius: "5px",
            border: "none",
            cursor: "pointer",
          }}
          onClick={() => setIsDialogOpen(true)}
        >
          Sell Your Art
        </button>
  
        {/* Profile Icon */}
        <div style={{ position: "relative" }}>
          <button
            style={{
              backgroundColor: "transparent",
              border: "none",
              cursor: "pointer",
              fontSize: "20px",
            }}
            onClick={() => setIsDialogOpen(true)}
          >
            <span role="img" aria-label="Profile">üßë</span>
          </button>
  
          {/* Profile Dropdown */}
          {showProfileDropdown && (
            <div
              style={{
                position: "absolute",
                top: "40px",
                right: "0",
                background: "white",
                boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                borderRadius: "5px",
                padding: "10px",
                width: "120px",
                textAlign: "center",
              }}
            >
              <p style={{ margin: "5px 0", cursor: "pointer" }}>Profile</p>
              <p style={{ margin: "5px 0", cursor: "pointer" }}>Logout</p>
            </div>
          )}
