        //       /> */}
        //       <img 
        //       src={`${art.imageUrl}?timestamp=${new Date().getTime()}`} 
        //       alt="Art" 
        //       style={{ width: '200px', height: '200px' }} 
        //     />


        //       <p><strong>Category:</strong> {art.category}</p>
        //       <p><strong>Description:</strong> {art.description}</p>
        //       <p><strong>Price:</strong> ‚Çπ{art.price}</p>
        //     </div>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
            gap: "20px",
            marginTop: "60px",
          }}
        >
          {artworks.map((art) => (
            <div
              key={art.id}
              style={{
                display: "flex",
                alignItems: "center",
                backgroundColor: "rgba(255, 255, 255, 0.9)",
                borderRadius: "10px",
                overflow: "hidden",
                boxShadow: "0px 4px 12px rgba(0,0,0,0.2)",
                transition: "transform 0.3s ease-in-out",
                cursor: "pointer",
              }}
              onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.03)")}
              onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
            >
              {/* Image Section */}
              <img
                src={`${art.imageUrl}?timestamp=${new Date().getTime()}`}
                alt="Art"
                style={{
                  width: "200px",
                  height: "200px",
                  borderRadius: "10px 0 0 10px",
                  objectFit: "cover",
                }}
              />

              {/* Details Section */}
              <div style={{ padding: "15px", flex: 1 }}>
                <h3 style={{ margin: "0", color: "#333" }}>{art.title}</h3>
                <p style={{ margin: "5px 0", color: "#777" }}>
                  <strong>Category:</strong> {art.category}
                </p>
                <p style={{ margin: "5px 0", color: "#777" }}>
                  <strong>Description:</strong> {art.description}
                </p>
                <p style={{ margin: "5px 0", color: "#333", fontWeight: "bold" }}>
                  ‚Çπ{art.price}
                </p>
              </div>
              </div>
          ))}
        </div>
      )}
      {selectedImage && (
  <div
    style={{
      position: "fixed",
      top: "0",
      left: "0",
      width: "100vw",
      height: "100vh",
      backgroundColor: "rgba(0, 0, 0, 0.9)",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      zIndex: 9999,
    }}
    onClick={() => setSelectedImage(null)}
  >
    <div
      style={{
        position: "relative",
        maxWidth: "100%",
        maxHeight: "100%",
      }}
      onClick={(e) => e.stopPropagation()} // Prevent modal from closing when clicking image
    >
      {/* Close Button */}
      <span
        onClick={() => setSelectedImage(null)}
        style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          fontSize: "30px",
          color: "white",
          cursor: "pointer",
          backgroundColor: "red",
          padding: "5px 10px",
          borderRadius: "50%",
          zIndex: 10000,
        }}
      >
        &times;
      </span>

      {/* Full-Screen Image */}
      <img
        src={`${selectedImage}?timestamp=${new Date().getTime()}`}
        alt="Large Art"
        style={{
          maxWidth: "95vw",
          maxHeight: "95vh",
          borderRadius: "10px",
          objectFit: "contain",
        }}
      />
    </div>
  </div>
)}



      {/* Artwork Upload Dialog */}
      {isDialogOpen && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "400px",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
          }}
        >
          <h2>Sell Your Art</h2>
          <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            <input
              type="text"
              name="title"
              placeholder="Title"
              value={formData.title}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            />
            <input
              type="text"
              name="category"
              placeholder="Category"
              value={formData.category}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={formData.description}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            />
             <input
              type="text"
              name="imageUrl"
              placeholder="Image URL"
              value={formData.imageUrl}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            /> 
            

            <input
              type="number"
              name="price"
              placeholder="Price"
              value={formData.price}
              onChange={handleInputChange}
              required
              style={{ padding: "8px", borderRadius: "5px", border: "1px solid #ddd" }}
            />
  
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <button type="submit" style={{ background: "#28a745", color: "white", padding: "8px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
                Upload
              </button>
              <button type="button" onClick={() => setIsDialogOpen(false)} style={{ background: "#dc3545", color: "white", padding: "8px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};
export default ArtistDashboard;
