const IconWithTooltip = ({ icon: Icon, tooltip, onClick }) => {
    const [showTooltip, setShowTooltip] = useState(false);

    return (
      <div
        style={{
          position: "relative",
          display: "inline-block",
          marginLeft: "20px",
        }}
        onMouseEnter={() => setShowTooltip(true)}
        onMouseLeave={() => setShowTooltip(false)}
        onClick={onClick}
      >
        {showTooltip && (
          <div
            style={{
              position: "absolute",
              top: "-35px",
              left: "50%",
              transform: "translateX(-50%)",
              backgroundColor: "green",
              color: "white",
              padding: "6px 12px",
              borderRadius: "6px",
              fontSize: "13px",
              whiteSpace: "nowrap",
              zIndex: 1,
              boxShadow: "0 2px 10px rgba(0,0,0,0.2)",
            }}
          >
            {tooltip}
          </div>
        )}
        <Icon size={30} style={{ cursor: "pointer", color: "white" }} />
      </div>
    );
  };

  return (
    <div className="page" style={{ padding: "20px", background: "#f0f0f0" }}>
      {/* Profile, Cart, Purchased Art Icons with Tooltip */}
      <div
        className="profile-cart-container"
        style={{
          display: "flex",
          justifyContent: "flex-end",
          marginBottom: "1rem",
          backgroundColor: "#333",
          padding: "10px",
          borderRadius: "8px",
        }}
      >
        <IconWithTooltip
          icon={FaImages}
          tooltip="Purchased Art"
          onClick={() => history.push("/purchased")}
        />
        <IconWithTooltip
          icon={FaShoppingCart}
          tooltip="View Cart"
          onClick={() => history.push("/cart")}
        />
        <IconWithTooltip
          icon={FaUserCircle}
          tooltip="Fill your profile"
          onClick={() => history.push("/userprofile")}
        />
      </div>

      {/* Header and Search */}
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          gap: "1rem",
          marginBottom: "2rem",
        }}
      >
        <h1 style={{ fontSize: "2rem", fontWeight: "bold" }}>Explore Artworks</h1>
        <input
          type="text"
          placeholder="Search by title or artist"
          className="search"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Artwork Grid */}
      <div className="art-grid" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {filteredArtworks.map((artwork, index) => (
          <div
            key={artwork.id || index}
            style={{
              background: "white",
              color: "black",
              borderRadius: "10px",
              overflow: "hidden",
              boxShadow: "0 4px 15px gold",
              height: "200px",
              width: "300px",
              display: "flex",
              alignItems: "center",
              flexDirection: "column",
              justifyContent: "center",
              marginBottom: "20px",
            }}
          >
            <img
              src={artwork.imageUrl}
              alt={artwork.title}
              className="card-img"
              onClick={() => handleImageClick(artwork)}
              style={{ cursor: "pointer", maxHeight: "100%", maxWidth: "100%" }}
            />
          </div>
        ))}

        {filteredArtworks.length === 0 && (
          <p className="no-data">No artworks found.</p>
        )}
      </div>

      {/* Modal */}
      {showModal && selectedArtwork && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <img
              src={selectedArtwork.imageUrl}
              alt={selectedArtwork.title}
              className="modal-img"
            />
            <h2 className="modal-title">{selectedArtwork.title}</h2>
            <p className="modal-desc">{selectedArtwork.description}</p>
            <p className="modal-price">
              Price: ₹{selectedArtwork.price?.toFixed(2)}
            </p>

            {artistDetails && (
              <>
                <p>
                  <strong>Artist:</strong> {artistDetails.name}
                </p>
                <p>
                  <strong>Rating:</strong> {artistDetails.rating} ⭐
                </p>
              </>
            )}

            <button className="btn" onClick={() => handleAddToCart(selectedArtwork)}>
              Add to Cart
            </button>
            <button className="close-btn" onClick={() => setShowModal(false)}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

