import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect,
  useLocation,
} from "react-router-dom";
import Header from "./components/Header";
import BackgroundVideo from "./components/BackgroundVideo";
import ArtistDashboard from "./components/board/ArtistDashboard";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";

function App() {
  const token = localStorage.getItem("token");
  const userRole = (localStorage.getItem("role") || "").toLowerCase();

  return (
    <Router>
      <AppContent token={token} userRole={userRole} />
    </Router>
  );
}

function AppContent({ token, userRole }) {
  const location = useLocation();

  return (
    <div className="app-container">
      {location.pathname === "/login" || location.pathname === "/register" ? (
        <BackgroundVideo />
      ) : null}

      <Header />

      <Switch>
        <Route exact path="/">
          {token ? <Redirect to="/dashboard" /> : <Redirect to="/login" />}
        </Route>

        <Route exact path="/login" component={Login} />
        <Route exact path="/register" component={Register} />

        {/* Define /dashboard route (or rename /artist-dashboard to /dashboard) */}
        <Route
          exact
          path="/dashboard"
          render={(props) =>
            token ? <ArtistDashboard {...props} /> : <Redirect to="/login" />
          }
        />

        {/* Catch-all */}
        <Route path="*">
          <h2>404 - Page Not Found</h2>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
