import React, { useEffect, useState } from "react";
import axios from "axios";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const response = await axios.get("http://localhost:8080/api/artworks", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log("‚úÖ Fetched Artworks:", response.data);
      setArtworks(response.data); // Store artworks in state
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  return (
    <div style={{ background: "white", minHeight: "100vh", padding: "20px" }}>
      <h1>Welcome to Art Gallery!</h1>

      {artworks.length === 0 ? (
        <p>Loading artworks...</p>
      ) : (
        <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "20px" }}>
          {artworks.map((art) => (
            <div
              key={art.id}
              style={{
                border: "1px solid #ddd",
                padding: "15px",
                borderRadius: "10px",
                boxShadow: "2px 2px 5px rgba(0,0,0,0.1)",
                textAlign: "center",
              }}
            >
              <h3>{art.title}</h3>
              <img src={art.imageUrl} alt={art.title} style={{ width: "100%", height: "200px", objectFit: "cover" }} />
              <p><strong>Price:</strong> ‚Çπ{art.price}</p>
              <p><strong>Description:</strong> {art.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
