import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaDownload, FaTrash } from "react-icons/fa";

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [purchasedIndexes, setPurchasedIndexes] = useState([]);

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/cart/items", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setCartItems(response.data);
      } catch (error) {
        console.error("Failed to fetch cart items:", error);
      }
    };

    fetchCartItems();
  }, []);

  const handleBuyNow = (index) => {
    setPurchasedIndexes([...purchasedIndexes, index]);
  };

  const handleDownload = (imageUrl, title) => {
    const link = document.createElement("a");
    link.href = imageUrl;
    link.download = `${title || "artwork"}.jpg`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleCancel = async (cartItemId, index) => {
    try {
      await axios.delete(`http://localhost:8080/api/cart/remove/${cartItemId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      const updatedItems = cartItems.filter((_, idx) => idx !== index);
      setCartItems(updatedItems);
      setPurchasedIndexes(purchasedIndexes.filter((i) => i !== index));
    } catch (error) {
      console.error("Error removing item from cart:", error);
    }
  };

  if (!cartItems || cartItems.length === 0) {
    return <h2 style={{ textAlign: "center", marginTop: "50px" }}>Your cart is empty.</h2>;
  }

  return (
    <div
      style={{
        padding: "20px",
        backgroundImage: `url("/website.png")`,
        backgroundSize: "100% 100%",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        backgroundAttachment: "fixed",
        minHeight: "100vh",
        width: "100vw",
        overflowX: "hidden",
      }}
    >
      <h2 style={{ marginBottom: "30px", textAlign: "center" }}>Shopping Cart</h2>
      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", gap: "30px" }}>
        {cartItems.map((item, index) => (
          <div
            key={index}
            style={{
              width: "280px",
              padding: "20px",
              borderRadius: "10px",
              backgroundColor: "#f9f9f9",
              boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
              display: "flex",
              flexDirection: "column",
              alignItems: "flex-start",
            }}
          >
            {item.imageUrl ? (
              <img
                src={item.imageUrl}
                alt={item.title || `Art ${index}`}
                style={{
                  width: "270px",
                  height: "200px",
                  objectFit: "cover",
                  borderRadius: "8px",
                  marginBottom: "10px",
                }}
              />
            ) : (
              <div
                style={{
                  width: "270px",
                  height: "200px",
                  backgroundColor: "#e0e0e0",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  borderRadius: "8px",
                  marginBottom: "10px",
                  color: "#666",
                  fontSize: "14px",
                }}
              >
                No Image Available
              </div>
            )}

            <h3 style={{ margin: "10px 0 5px" }}>{item.title || "Artwork"}</h3>
            <p style={{ margin: "0 0 10px" }}>{item.price ? `â‚¹${item.price}` : ""}</p>

            {!purchasedIndexes.includes(index) ? (
              <button
                onClick={() => handleBuyNow(index)}
                style={{
                  padding: "10px 20px",
                  fontSize: "16px",
                  backgroundColor: "#28a745",
                  color: "white",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                  marginBottom: "10px",
                }}
              >
                Buy Now
              </button>
            ) : (
              <button
                onClick={() => handleDownload(item.imageUrl, item.title)}
                style={{
                  padding: "10px 20px",
                  fontSize: "16px",
                  backgroundColor: "#ffcc00",
                  color: "black",
                  border: "none",
                  borderRadius: "5px",
                  display: "flex",
                  alignItems: "center",
                  gap: "8px",
                  cursor: "pointer",
                  marginBottom: "10px",
                }}
              >
                <FaDownload /> Download
              </button>
            )}

            <button
              onClick={() => handleCancel(item.id, index)}
              style={{
                padding: "6px 12px",
                fontSize: "14px",
                backgroundColor: "#dc3545",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
                display: "flex",
                alignItems: "center",
                gap: "6px",
              }}
            >
              <FaTrash />
              Cancel
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CartPage;
