import React from "react";
import { BrowserRouter as Router, Route, Switch, Redirect, useLocation } from "react-router-dom";
import Header from "./components/Header";
import BackgroundVideo from "./components/BackgroundVideo";
import ArtistDashboard from "./components/board/ArtistDashboard";
import ArtistProfile from "./components/board/ArtistProfile";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";

function App() {
  const token = localStorage.getItem("token"); // üîë Check if user is logged in
  const userRole = (localStorage.getItem("role") || "").toLowerCase(); // üé≠ Get user role

  return (
    <Router>
      <AppContent token={token} userRole={userRole} />
    </Router>
  );
}

function AppContent({ token, userRole }) {
  const location = useLocation(); // üîç Get current path

  return (
    <div className="app-container">
      {/* ‚úÖ Show Background Video only on Login/Register */}
      {location.pathname === "/login" || location.pathname === "/register" ? (
        <BackgroundVideo />
      ) : null}

      <Header /> {/* Always show Header */}

      <Switch>
        {/* üîπ Redirect to artist dashboard if logged in, else to login */}
        <Route exact path="/">
          {token ? <Redirect to="/artist-dashboard" /> : <Redirect to="/login" />}
        </Route>

        {/* üîπ Authentication routes */}
        <Route exact path="/login" component={Login} />
        <Route exact path="/register" component={Register} />

        {/* üîπ Artist Dashboard - Only visible if logged in */}
        <Route
          exact
          path="/artist-dashboard"
          render={(props) =>
            token ? <ArtistDashboard {...props} /> : <Redirect to="/login" />
          }
        />

        {/* üîπ Catch-all for undefined routes */}
        <Route path="*">
          <h2>404 - Page Not Found</h2>
        </Route>
      </Switch>
    </div>
  );
}

export default App;
