import React, { useEffect, useState } from 'react';
import axios from 'axios';

function OrderStatusPage() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8080/api/admin/dashboard/orders', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setOrders(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const updateStatus = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.put(
        `http://localhost:8080/api/admin/dashboard/orders/${orderId}/status/${newStatus}`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert(`Order status updated to ${newStatus}`);
      fetchOrders(); // Refresh data
    } catch (error) {
      console.error('Failed to update status:', error);
      alert('Failed to update order status.');
    }
  };

  if (loading) return <p className="text-center mt-4">Loading orders...</p>;

  return (
    <div className="p-6 text-white">
      <h2 className="text-2xl font-bold mb-6">Order Status</h2>

      <table className="w-full border-collapse bg-gray-800 rounded-md overflow-hidden">
        <thead className="bg-gray-700">
          <tr>
            <th className="border px-6 py-3 text-left">ID</th>
            <th className="border px-6 py-3 text-left">Order Status</th>
            <th className="border px-6 py-3 text-left">Order Date</th>
            <th className="border px-6 py-3 text-left">Total Price</th>
            <th className="border px-6 py-3 text-left">Change Status</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order, index) => (
            <tr key={order.id} className={index % 2 === 0 ? 'bg-gray-900' : 'bg-gray-800'}>
              <td className="border px-6 py-3">{order.id}</td>
              <td className="border px-6 py-3">{order.orderStatus}</td>
              <td className="border px-6 py-3">{order.orderDate}</td>
              <td className="border px-6 py-3">₹{order.totalPrice}</td>
              <td className="border px-6 py-3">
                <select
                  onChange={(e) => updateStatus(order.id, e.target.value)}
                  defaultValue=""
                  className="bg-gray-700 text-white px-3 py-1 rounded"
                >
                  <option value="" disabled>Update</option>
                  <option value="PENDING">Pending</option>
                  <option value="COMPLETED">Completed</option>
                  <option value="CANCELLED">Cancelled</option>
                  <option value="FAILED">Failed</option>
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default OrderStatusPage;
