import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import "./UserDashboard.css";

const UserDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const history = useHistory();

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const response = await axios.get("/api/artwork");
        setArtworks(response.data);
      } catch (error) {
        console.error("Error fetching artworks:", error);
      }
    };
    fetchArtworks();
  }, []);

  const handleAddToCart = (artwork) => {
    console.log("Adding to cart:", artwork);
    history.push("/cart");
  };

  const filteredArtworks = artworks.filter(
    (art) =>
      art.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      art.artist?.username?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="page">
      {/* Profile Icon */}
      <div className="profile-icon">
        <div className="tooltip">Fill your profile</div>
        <FaUserCircle
          size={32}
          className="icon"
          onClick={() => history.push("/profile")}
        />
      </div>

      {/* Header and Search */}
      <div className="header">
        <h1 className="title">Explore Artworks</h1>
        <input
          type="text"
          placeholder="Search by title or artist"
          className="search"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Artwork Cards */}
      <div className="art-grid">
        {filteredArtworks.map((art) => (
          <div key={art.id} className="card">
            <img src={art.imageUrl} alt={art.title} className="card-img" />
            <div className="card-body">
              <h2 className="card-title">{art.title}</h2>
              <p className="card-desc">{art.description}</p>
              <p className="card-price">â‚¹{art.price?.toFixed(2)}</p>
              <p className="card-artist">Artist: {art.artist?.username}</p>
              <button
                className="btn"
                onClick={() => handleAddToCart(art)}
              >
                Add to Cart
              </button>
            </div>
          </div>
        ))}

        {filteredArtworks.length === 0 && (
          <p className="no-data">No artworks found.</p>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
