import React, { useState, useEffect } from "react";
import Register from "./auth/Register";
import Login from "./auth/Login";
import "./Header.css";
import { useHistory } from "react-router-dom";

const Header = () => {
  const history = useHistory();
  const [openRegister, setOpenRegister] = useState(false);
  const [openLogin, setOpenLogin] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState("");
  const [userRole, setUserRole] = useState("");

  // âœ… Check authentication and role on component mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedUsername = localStorage.getItem("username");
    const role = localStorage.getItem("role"); // âœ… Get stored user role

    if (token) {
      setIsAuthenticated(true);
      setUsername(storedUsername || "User");
      setUserRole(role || "user"); // Default to "user" if no role is found
    }
  }, []);

  // âœ… Handle login
  const handleLogin = (token) => {
    console.log("âœ… User logged in with token:", token);
    setIsAuthenticated(true);
    setUsername(localStorage.getItem("username") || "User");
    setUserRole(localStorage.getItem("role") || "user");
  };

  // âœ… Handle logout
  const handleLogout = () => {
    console.log("ðŸšª Logging out...");
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("username");

    setIsAuthenticated(false);
    setUsername("");
    setUserRole("");
    history.push("/"); // Redirect to homepage
  };

  // âœ… Navigate to the correct dashboard
  const navigateToDashboard = () => {
    if (userRole === "artist") {
      history.push("/artist-dashboard"); // âœ… Redirect artists
    } else {
      history.push("/dashboard"); // âœ… Redirect other users
    }
  };

  return (
    <header className="tinted-header">
      <h1 className="header-title">Welcome To ArtFusion</h1>

      <div className="button-container">
        {isAuthenticated ? (
          <>
            <span className="welcome-text">Hello, {username}! ðŸŽ¨</span>

            {/* âœ… Show Artist Dashboard button if role is "artist" */}
            <button className="auth-button" onClick={navigateToDashboard}>
              {userRole === "artist" ? "Artist Dashboard" : "Dashboard"}
            </button>

            <button className="auth-button logout-button" onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <>
            <button className="auth-button" onClick={() => setOpenRegister(true)}>Register</button>
            <button className="auth-button" onClick={() => setOpenLogin(true)}>Login</button>
          </>
        )}
      </div>

      {/* Signup Dialog */}
      <Register open={openRegister} onClose={() => setOpenRegister(false)} />

      {/* âœ… Login Dialog (Pass `onLogin`) */}
      <Login open={openLogin} onClose={() => setOpenLogin(false)} onLogin={handleLogin} />
    </header>
  );
};

export default Header;
