import React, { useEffect, useState } from "react";
import axios from "axios";

export default function ArtistDashboard() {
  const [artworks, setArtworks] = useState([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [artistName, setArtistName] = useState("");
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      window.location.href = "/login";
      return;
    }

    try {
      const response = await axios.get("http://localhost:8080/api/artworks/my-artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data.artworks);
      setArtistName(response.data.artistName);
      setIsLoading(false);
    } catch (error) {
      console.error("Error fetching artworks", error);
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  return (
    <div style={{ padding: "20px", backgroundColor: "#f4f4f4", minHeight: "100vh" }}>
      <header style={{ display: "flex", justifyContent: "space-between" }}>
        <h1>Welcome, {artistName}!</h1>
        <div>
          <button onClick={() => setShowProfileDropdown(!showProfileDropdown)}>
            Profile
          </button>
          {showProfileDropdown && (
            <div>
              <p onClick={handleLogout}>Logout</p>
            </div>
          )}
        </div>
      </header>

      <button onClick={() => setIsDialogOpen(true)}>Sell Your Art</button>

      {isDialogOpen && (
        <div style={{ position: "fixed", background: "white", padding: "20px" }}>
          <h2>Sell Your Art</h2>
          <button onClick={() => setIsDialogOpen(false)}>Close</button>
        </div>
      )}

      <h2>Your Artworks</h2>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {artworks.map((art) => (
            <div key={art.id}>
              <h3>{art.title}</h3>
              <p>{art.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
