import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    category: "",
    description: "",
    image: null,
    price: "",
  });

  const history = useHistory();

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const response = await axios.get("http://localhost:8080/api/artworks/my-artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const formDataToSend = new FormData();
      formDataToSend.append("title", formData.title);
      formDataToSend.append("category", formData.category);
      formDataToSend.append("description", formData.description);
      formDataToSend.append("image", formData.image);
      formDataToSend.append("price", formData.price);

      await axios.post("http://localhost:8080/api/artworks/upload", formDataToSend, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });

      alert("‚úÖ Artwork uploaded successfully!");
      setIsDialogOpen(false);
      fetchArtworks();
    } catch (error) {
      console.error("üî¥ Error uploading artwork:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    history.push("/");
  };

  return (
    <div
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/room.jpg)` ,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        padding: "20px",
        opacity: 0.9
      }}
    >

      {/* Header Section */}
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
          gap: "15px",
          padding: "10px",
        }}
      >
        {/* Sell Your Art Button */}
        <button
          style={{ backgroundColor: "#007bff", color: "white", padding: "10px 15px", borderRadius: "5px", border: "none", cursor: "pointer" }}
          onClick={() => setIsDialogOpen(true)}
        >
          Sell Your Art
        </button>

        {/* Profile Icon */}
        <div style={{ position: "relative" }}>
          <button
            style={{ backgroundColor: "transparent", border: "none", cursor: "pointer", fontSize: "20px" }}
            onClick={() => setShowProfileDropdown(!showProfileDropdown)}
          >
            <span role="img" aria-label="Profile">üßë</span>
          </button>

          {/* Profile Dropdown */}
          {showProfileDropdown && (
            <div
              style={{
                position: "absolute",
                top: "40px",
                right: "0",
                background: "white",
                boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                borderRadius: "5px",
                padding: "10px",
                width: "120px",
                textAlign: "center",
              }}
            >
              <p style={{ margin: "5px 0", cursor: "pointer" }} onClick={() => history.push("/profile")}>Profile</p>
              <p style={{ margin: "5px 0", cursor: "pointer" }} onClick={handleLogout}>Logout</p>
            </div>
          )}
        </div>
      </div>

      <h1
        style={{ color: "white", position: "absolute", top: "20px", left: "20px", margin: "0", fontSize: "28px", fontWeight: "bold" }}
      >
        Welcome to Art Gallery!
      </h1>

      {/* Artworks Grid */}
      {artworks.length === 0 ? (
        <p>Loading artworks...</p>
      ) : (
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", gap: "20px" }}>
          {artworks.map((art) => (
            <div key={art.id} style={{ border: "1px solid #ddd", padding: "15px", borderRadius: "8px", textAlign: "center", boxShadow: "0px 4px 8px rgba(0,0,0,0.2)" }}>
              <h3>{art.title}</h3>
              <img src={`${art.imageUrl}?timestamp=${new Date().getTime()}`} alt="Art" style={{ width: '200px', height: '200px' }} />
              <p><strong>Category:</strong> {art.category}</p>
              <p><strong>Description:</strong> {art.description}</p>
              <p><strong>Price:</strong> ‚Çπ{art.price}</p>
            </div>
          ))}
        </div>
      )}

      {/* Artwork Upload Dialog */}
      {isDialogOpen && (
        <div style={{ position: "fixed", top: "50%", left: "50%", transform: "translate(-50%, -50%)", background: "white", padding: "20px", borderRadius: "10px" }}>
          <h2>Sell Your Art</h2>
          <form onSubmit={handleSubmit}>
            <input type="text" name="title" placeholder="Title" onChange={handleInputChange} required />
            <input type="file" name="image" onChange={handleFileChange} required />
            <button type="submit">Upload</button>
            <button onClick={() => setIsDialogOpen(false)}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
