import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ArtistDashboard.css"; // âœ… Import CSS for styling

const ArtistDashboard = () => {
  const [artistName, setArtistName] = useState("");
  const [artworks, setArtworks] = useState([]); 
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true); // âœ… Add loading state

  useEffect(() => {
    const storedName = localStorage.getItem("artistName") || "Artist"; 
    setArtistName(storedName);

    const fetchArtworks = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token"); // âœ… Get auth token (if required)
        const response = await axios.get("http://localhost:8080/api/artworks", {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // âœ… Include token if required
          },
        });

        console.log("ðŸŽ¨ ArtistDashboard Component Loaded");
        setArtworks(response.data); 
      } catch (err) {
        console.error("ðŸ”´ Error fetching artworks:", err);
        setError("Failed to load artworks. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchArtworks();
  }, []);

  return (
    <div className="artist-dashboard">
      <h1 className="welcome-text">Welcome {artistName} to ArtFusion!</h1>
      <h2 className="artist-quote">An artist is not paid for his labour but for his vision...</h2>

      {/* âœ… Display Error Message */}
      {error && <p className="error-message">{error}</p>}

      {/* âœ… Show Loader While Fetching */}
      {loading ? (
        <p className="loading-text">Fetching artworks, please wait...</p>
      ) : (
        <div className="artworks-container">
          {artworks.length > 0 ? (
            artworks.map((artwork) => (
              <div key={artwork.id} className="artwork-card">
                <img src={artwork.imageUrl} alt={artwork.title} className="artwork-image" />
                <h3 className="artwork-title">{artwork.title}</h3>
                <p className="artwork-description">{artwork.description}</p>
              </div>
            ))
          ) : (
            <p className="no-artworks">No artworks available at the moment.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
