import React, { useState } from "react";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "axios";

const CheckoutForm = ({ artworkId, clientSecret }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [downloadUrl, setDownloadUrl] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements || !clientSecret) {
      setErrorMessage("Stripe not ready or missing client secret.");
      return;
    }

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (result.error) {
      setErrorMessage(result.error.message);
      alert("Payment failed!");
    } else {
      if (result.paymentIntent.status === "succeeded") {
        alert("‚úÖ Payment successful!");

        try {
          const response = await axios.get(`http://localhost:8080/api/artworks/download/${artworkId}`);
          console.log("üîé Response from backend:", response.data); // ‚úÖ Debug log

          // Use artworkUrl or downloadUrl ‚Äî depending on backend key
          const url = response.data.downloadUrl || response.data.artworkUrl;

          if (response.status === 200 && url) {
            setDownloadUrl(url);
          } else {
            setErrorMessage("You are not eligible to download this artwork.");
          }
        } catch (error) {
          console.error("‚ùå Error fetching download link:", error);
          setErrorMessage("An error occurred while checking download eligibility.");
        }
      }
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} style={{ marginTop: "30px" }}>
        <CardElement />
        <button type="submit" disabled={!stripe} style={{ marginTop: "20px" }}>
          Pay Now
        </button>
      </form>

      {downloadUrl && (
        <div>
          <h3>Your payment was successful!</h3>
          <button
            onClick={() => window.open(downloadUrl, "_blank")}
            style={{ marginTop: "20px" }}
          >
            Download Artwork
          </button>
        </div>
      )}

      {errorMessage && (
        <div style={{ color: "red", marginTop: "20px" }}>
          <p>{errorMessage}</p>
        </div>
      )}
    </div>
  );
};

export default CheckoutForm;
