import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const UserProfile = () => {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [contactNumber, setContactNumber] = useState("");
  const [saved, setSaved] = useState(false);

  const getInitials = (text) => {
    if (!text) return "U";
    return text.charAt(0).toUpperCase();
  };

  const handleSave = async (e) => {
    e.preventDefault();

    if (email && contactNumber) {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          alert("You are not logged in.");
          return;
        }

        const response = await axios.post(
          "http://localhost:8080/api/buyers/create", // Replace with your backend URL if needed
          {
            email,
            contactNumber,
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (response.status === 200) {
          setSaved(true);
          alert("Buyer profile created successfully!");
        }
      } catch (error) {
        console.error("Error:", error);
        alert("Failed to create profile. Try again.");
      }
    } else {
      alert("Please fill in all fields.");
    }
  };

  return (
    <div
      style={{
        display: "flex",
        minHeight: "100vh",
        backgroundColor: "#f0f0f0",
        padding: "20px",
      }}
    >
      {/* Profile Preview */}
      {saved && (
        <div
          style={{
            width: "300px",
            backgroundColor: "#fff",
            borderRadius: "10px",
            padding: "20px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            marginRight: "30px",
            textAlign: "center",
          }}
        >
          <div
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "50%",
              backgroundColor: "#007bff",
              color: "white",
              fontSize: "36px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              margin: "0 auto 15px",
            }}
          >
            {getInitials(email)}
          </div>
          <h3>{email}</h3>
          <p>{contactNumber}</p>
        </div>
      )}

      {/* Form Section */}
      <form
        onSubmit={handleSave}
        style={{
          flex: 1,
          backgroundColor: "white",
          borderRadius: "10px",
          padding: "30px",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ marginBottom: "20px" }}>Create Buyer Profile</h2>

        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{
            width: "100%",
            padding: "10px",
            marginBottom: "20px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />

        <label>Phone Number:</label>
        <input
          type="tel"
          value={contactNumber}
          onChange={(e) => setContactNumber(e.target.value)}
          style={{
            width: "100%",
            padding: "10px",
            marginBottom: "20px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />

        <button
          type="submit"
          style={{
            padding: "10px 20px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Save Profile
        </button>

        <button
          type="button"
          onClick={() => navigate("/dashboard")}
          style={{
            marginLeft: "10px",
            padding: "10px 20px",
            backgroundColor: "#ccc",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Back to Dashboard
        </button>
      </form>
    </div>
  );
};

export default UserProfile;
